INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (69451, 11, 'Школьные курсы и егэ по математике', false, '<p>Уважаемые ученики, дистанционный  курс по алгебре создан, чтобы помочь вам повторить различные темы  в помощь в подготовке к ОГЭ. Каждый урок будет включать в себя:</p>

