INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (69105, 7, 'Программирование и анализ данных для продолжающих', false, '<p>Курс по Web - программированию представляет собой серию уроков, объединенных в несколько разделов подкрепленных теоретическими и практическими заданиями.  В конце каждой темы предлагается выполнить домашнее задание, которое позволит закрепить пройденное.</p>

