INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (67994, 13, 'Мусор2', false, '<p>Курс «Анатомия и физиология человека» позволит вам не только узнать или уточнить строение человеческого тела, но и познакомиться с различными физиологическими процессами, происходящими в нашем организме. Курс практикоориентирован, поэтому вас ждут замечательные практические задания и комментарии для их выполнения, вы узнаете правила оказания первой доврачебной помощи. Программа включает отдельные содержательные блоки, каждый из которых - это круг вопросов, связанных со строением и функциями конкретного аппарата или системы. А девиз нашего курса: «Недостаточно только получить знания; надо найти им приложение. Недостаточно только желать; надо делать». (Гёте И)</p>

