INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (63510, 13, 'Мусор2', false, '<p>РАЗДЕЛ 1. Архитектура экономических информационных систем Тема 1. Понятие и классификация экономических информационных систем (ЭИС).  Тема 2. Функциональные подсистемы ЭИС.  Тема 3. Обеспечивающие подсистемы ЭИС. <br>
 <br>
