INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (62383, 5, 'Введение в основы программирования и анализ данных', false, '<p>На этом курсе вы изучите основы языка программирования PHP. PHP используется для разработки веб-приложений. На сегодняшний день PHP лидирует в сфере веб-технологий, более 80% сайтов написаны на PHP.</p>

