INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (62709, 1, 'Мусор1', true, '<p>Вы стали руководителем и теперь отвечаете не только за себя, но и за свою команду, своих сотрудников.</p>

