INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (61441, 13, 'Мусор2', false, '<p>Участники олимпиад часто сталкиваются с задачами на определение веществ в практических турах олимпиад, в теоретических - с хитроумными цепочками реакций. Задача этого курса - напомнить что практические и теоретические познания дополняют друг друга и дать необходимые инструменты для аналитического исследования органических соединений в виду специфики поля деятельности.</p>

