INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (57818, 1, 'Мусор1', false, '<p>The rapidly developing artificial intelligence technologies are opening whole new worlds of opportunities with endless possibilities. From self-driving cars, virtual assistants to medical diagnosis; products and services that were not possible a few years ago are now unleashed by machine learning and AI.</p>

