INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (57511, 10, 'Правоведение, психология, история', false, '<p>     Онлайн-курс "Психология экстремальных ситуаций" содержит характеристики особенностей психического состояния и поведения пострадавших в чрезвычайных ситуациях, общие принципы, особенности и алгоритмы общения с ними специалистов. Главное внимание обращается на умения оценивать психическое состояние пострадавших, оказывать первую психологическую помощь, вести информационно-разъяснительную работу. При этом отмечается важность сохранения собственного психического здоровья, поскольку специалисты, работающие в экстремальных условиях, подвергаются воздействию большого количества стрессоров. Знание принципов профилактики негативных последствий профессионального стресса и способов саморегуляции психологического состояния позволит повысить уровень стрессоустойчивости.</p>

