INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (57010, 13, 'Мусор2', false, '<p><strong>Компьютерная лингводидактика</strong> является междисциплинарной областью знания и тесно взаимодействует с развитием информа­ционных технологий, прикладной и математической лингвистики, разработками в области искусственного интеллекта, дизайна ком­пьютерных программ, исследований взаимодействия «человек — компьютер», теорией и практикой компьютерного обучения в це­лом. </p>

<p>Эта дисциплина сформировалась в качестве самостоятельно­го направления в преподавании языков с конца 80-х годов XX века и в ней получили развитие три направления исследований:</p>

<p>1) раз­работка теоретических аспектов использования компьютеров в обучении языку (методологические проблемы компьютерной лингводидактики,   типология   компьютерных  учебных   материалов, оценка эффективности компьютерных средств обучения и др.);</p>

<p>2) экспериментальная работа по созданию и использованию в учебном процессе компьютерных материалов для различных це­лей, этапов и профилей обучения языку;</p>

<p>3) пути интеграции ком­пьютерного обучения в общий процесс обучения языку.</p>
', '{38653864}', 'self_paced', 160, 36, 12690, 'ru', 'Всем, кто хочет изучать использование информационно-коммуникационных технологий в обучении.', false, 5, 'Компьютерная лингводидактика', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/57010/cover_PBlrJ9z/7f8dd34e07d84776d3893b3993cbb774.png');
