INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (56664, 1, 'Мусор1', false, '<p>В основу курса легли знания/опыт полученным ведущим от своих наставников и учителей, а так же собственного многолетнего  опыта в практике йоги и обучение йоге.</p>

