INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (57029, 7, 'Программирование и анализ данных для продолжающих', false, '<p>Специалисты «КСБ-СОФТ» выполнили свыше 3 000 проектов по информационной безопасности, включая экспертные аудиты, подготовку и сопровождение организаций при проверках регуляторов. На основе базы знаний компании был создан образовательный проект, в рамках которого выпускаются обучающие курсы по обработке и защите персональных данных в сфере здравоохранения, образования и других сферах.</p>

<p>В результате изучения данного курса сотрудники медицинских организаций получат знания об особенностях обработки персональных данных и научатся ориентироваться в требованиях законодательства. </p>

<p>Материалы курса содержат информацию из открытых ресурсов.</p>
', '{114243826}', 'self_paced', 852, 26, 748, 'ru', 'Курс предназначен для работников медицинских организаций, которые имеют доступ к персональным данным пациентов и хотят уверенно ориентироваться в законодательных требованиях в данной области.', false, 5, 'Обработка персональных данных в медицинских организациях', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/57029/cover_8SDUst6/4c4acdd475bb54534123146198fd97c7.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (57020, 17, 'Обучение, управление (кластер не очень)', false, '<p>Главной целью онлайн-курса является представление историко-культурного наследия Ярославской области и практического опыта в сфере его охраны. Ярославская область имеет богатое культурное наследие разных веков, следовательно, показывает не только разнообразие памятников, но и многочисленные аспекты и проблемы их изучения, охраны, презентации и использования. Онлайн-курс направлен на формирование и углубление профессиональных компетенций в сфере изучения, охраны и использования объектов историко-культурного наследия на примере Ярославля и Ярославской области; на развитие государственно-общественного и государственно-частного партнерства в сфере охраны объектов историко-культурного наследия. Онлайн-курс состоит из видеолекций, которые сопровождаются иллюстративными материалами и тестами для контроля знаний учащихся.</p>
', '{111681933}', 'self_paced', 243, 140, 23025, 'ru', '- обучающиеся по программам бакалавриата, магистратуры и аспирантуры, связанным с изучением и охраной историко-культурного наследия;
- архитекторы и реставраторы;
- застройщики;
- общественные инспектора, экскурсоводы, музейные работники;
- граждане РФ, интересующиеся проблемами истории и культуры.', false, 4.77777777777778, 'Охрана объектов культурного наследия: Ярославская область', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/57020/cover_sjXgdYj/9ab15d5b8ddb994d451034c73df096e8.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (57007, 17, 'Обучение, управление (кластер не очень)', true, '<p>В курсе описываются современные энергетические технологии переработки топливных ресурсов. Рассматриваются свойства и процессы горения натуральных топлив, методы подготовки и сжигания композитных топлив, различные технологии газификации твердых топлив, практика проектирования систем переработки топлива.</p>

<p>Курс позволит слушателям расширить профессиональный кругозор, определить направление профессиональной подготовки.</p>

<p>Слушатели получат знания о свойствах, процессах и перспективных технологиях использования твердого топлива для решения задач устойчивого энергоснабжения при минимальном воздействии на окружающую среду.</p>
', '{47702322}', 'active', 422, 142, 13028, 'ru', 'Курс предназначен для бакалавров и специалистов различных направлений в области теплоэнергетики, теплотехники, энергомашиностроения и химических технологий. А также всех интересующихся темой экоэнергетики.', false, 5, 'Чистая энергетика на твердом топливе – это реально', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/57007/cover_PGyTSTI/b42f11bd0ba55e392f7c97ef02b2ee4d.png');
