INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (54537, 17, 'Обучение, управление (кластер не очень)', false, 'Курс "Выбор видеооборудования" познакомит вас с основными моментами на которые нужно обратить внимание при проектировании систем видеонаблюдения и СКУД.  Вы не просто узнаете какие параметры оборудования сейчас активно продвигаются продавцами, а научитесь разбираться какие из них необходимы для решения именно ваших задач. <br>Курс будет полезен как менеджерам по продажам оборудования, так и пользователям, проектирующим систему видеонаблюдения для себя.', '{99011809}', 'self_paced', 133, 15, 1981, 'ru', 'NA', false, 5, 'Выбор видеооборудования', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/54537/cover_J2oACmT/bc5c5c56aed8a83af4257cf3e88d6491.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (56883, 2, 'Математический анализ, статистика', false, '<p>Курс дает представление о методах сбора и обработки маркетинговой информации, вводит в суть способов изучения поведения потребителей и конкурентов, рассказывает об основных моделях анализа рынка.</p>

<p>В материалах курса Вы найдёте теоретические выкладки, подкреплённые простыми примерами, ссылки на используемую литературу и источники информации.</p>

<p>Тесты основаны на представленных в курсе материалах и рассчитаны на повторение и запоминание. В конце каждого модуля Вам будет предложено открытое задание, в рамках которого необходимо написать эссе, рецензировать несколько работ учащихся и дождаться рецензий на свою работу.</p>

<p>Экзамен состоит из тестовых вопросов с закрытыми ответами.</p>

<p>Видеоматериалы в данном курсе не предусмотрены.</p>
', '{124475972}', 'self_paced', 1035, 31, 14362, 'ru', 'Курс ориентирован на студентов, получающих образование по специальностям «Реклама и связи с общественностью», «Торговое дело», «Сервис», «Туризм».

Материалы курса рекомендованы преподавателям, использующим смешанную модель обучения, в качестве дополнения к аудиторным занятиям.', false, 5, 'Маркетинговые исследования и ситуационный анализ', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/56883/cover_6zWp8Tm/46b329586980882ddbf825b2f425ff80.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (56856, 9, 'Экономика, менеджмент', false, '<p><strong>Этот междисциплинарный прикладной курс для Вас, если Вы:</strong></p>

<ul>
	<li>слушатель университета и хотите познакомиться с контурами инновационной экономики, чтобы как можно быстрее начать использовать эти знания в проектной работе или профессиональной деятельности;</li>
	<li>из бизнес-среды, преподаватель Университета или представитель органов власти – для повышения квалификации, обмена опытом и создания коллаборационных проектов;</li>
	<li>просто заскучали в потоке информации о происходящем вокруг и есть желание ее структурировать, найти собеседников.</li>
</ul>

<p><strong>Что делаем:</strong><br>
Знакомимся с теорией и практикой инновационной экономики, учимся работать с будущим и упаковывать тренды в образы клиентов и отраслей.<br>
Этот курс – первый шаг к разработке стратегий компаний в условиях постоянно изменяющейся среды инновационной экономики</p>

<p><strong>Самое главное, чему научитесь:</strong></p>

<ol>
	<li>Понимать инновационную экономику.</li>
	<li>Работать с потоком информации о глобальной среде.</li>
	<li>Упаковывать его для разработки стратегий.</li>
	<li>Быть готовым ставить долгосрочные приоритеты в своем профессиональном развитии.</li>
</ol>
