INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (55920, 13, 'Мусор2', false, 'Ass', '{28787895}', 'self_paced', 571, 64, 9398, 'ru', 'Курс предлагается для студентов медицинских ВУЗов и колледжей (высшее и среднее профессиональное образование), практических врачей, специалистов в области патологии и патофизиологии', false, 5, 'Решение задач по патологии', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/55920/cover_7g12Sep/471fe27a94788646557c2319d29b91fb.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (51141, 17, 'Обучение, управление (кластер не очень)', false, '<p>Курс посвящен основам теории государства. Освещает такие вопросы, как:</p><p>- понятие и признаки государства</p><p>- теории происхождения государства</p><p>- виды государств по форме правления, форме государственного устройства, политическому режиму</p>', '{18120598}', 'self_paced', 297, 17, 5383, 'ru', 'NA', false, 5, 'Государство: понятие, признаки, виды', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (51, 12, 'Мне повезет', true, '<p>В мире записаны многие десятки тысяч фольклорных текстов. Под фольклором я имею в виду не только развернутые тексты (сказки, легенды, былички и др.), но и разного рода народные верования типа представлений о небесных светилах и атмосферных явлениях (радуге, солнце, луне, созвездиях и т.п.).</p>

<p>Фольклористика подходила к фольклору некоторой данной традиции как к своеобразному коду, который нуждался в интерпретации и позволял, например, соотнести содержание фольклорных текстов и некую этнографическую реальность. Этот подход подход оказался не очень продуктивен, а рассмотрение мирового фольклора как целого до сих пор было вне фокуса внимания. Между тем, весь массив известных нам текстов содержат огромный объем информации. О чем она?</p>

<p>Проблема интерпретации большого количества данных касается не только фольклора, но и вообще массового материала по человеческой культуре — например, данных об одежде, пище, жилище, верованиях, обычаях — обо всем, что изучают антропологи в разных обществах. Попытка работать с большим массивом этнографических материалов была предпринята в середине ХХ века Джорджем Мёрдоком. В результате сведения, взятые из многих сотен этнографических описаний разных культур мира, оказались расклассифицированы с опорой на каталог признаков, элементов культуры. Туда попадают, среди прочего, и позы, которые характерны для представителей различных культур, и идеал телесной красоты, и использование ароматов, и ритмы в танцах, и, например, данные о том, подвергают ли в этом обществе стерилизации людей с отклонениями.</p>

<p>Данные по фольклору и мифологии в базу данных Мердока не включены. Моя задача была восполнить эту лакуну. Первоначально, в начале 1980-х годов, речь шла об обработке данных по мифологии и фольклору Южной Америки, потом — Центральной Америки и Мексики, потом — Северной Америки, ну и потом уже все остальное.</p>

<p>Накладывая на карту данные о распределении мотивов и образов (элементов фольклора), мы можем с некоторой вероятностью определить направление культурных контактов в отдаленном прошлом и даже представить себе, как шло заселение больших территорий. В этом курсе я познакомлю вас с основными принципами, которые легли в основу составляемой базы данных по мировому фольклору. Мы увидим, как с этой базой можно работать, и на нескольких примерах станет понятно, какого рода закономерности можно проследить на этом материале.</p>

<p><a href="https://stepik.org/media/attachments/course/51/%D0%90%D1%80%D1%85%D0%B5%D0%BE%D0%BB%D0%BE%D0%B3%D0%B8%D1%8F_%D1%84%D0%BE%D0%BB%D1%8C%D0%BA%D0%BB%D0%BE%D1%80%D0%B0.pdf" rel="noopener noreferrer nofollow">Методическая информация</a></p>
