INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (54849, 3, 'Огэ, егэ (без математики)', false, '<p>Перед вами пятидневный курс-тренажёр с большим количеством заданий формата ЕГЭ. <br>Эти задания лучше проходить сразу после просмотра стримов (вебинаров) предбанника на канале "Соточка по русскому (﻿<a href="https://www.youtube.com/channel/UCXzJhu8qo7n2h7xG8k12J3g?view_as=subscriber" rel="nofollow noopener noreferrer" title="Link: https://www.youtube.com/channel/UCXzJhu8qo7n2h7xG8k12J3g?view_as=subscriber">https://www.youtube.com/channel/UCXzJhu8qo7n2h7xG8k12J3g?view_as=subscriber</a>)﻿. ﻿<br><br>Задания составлены так, чтобы охватить как можно больше вариантов одного и того же задания и максимум исключений. <br>Также на курсе можно поработать с текстами (поискать проблемы и позиции автора) и ознакомиться с 12-ю идеальными сочинениями формата ЕГЭ (на 24 балла), которые написаны либо автором курса / либо 11-классниками / либо ﻿являются работами с досрочного экзамена. ﻿<br><br>﻿Курс для тех, кто хочет вспомнить всё и хочет быстро получать ответы на волнующие вопросы.<br></p>', '{26533986}', 'self_paced', 180, 317, 34383, 'ru', 'NA', true, 5, 'Предбанник (ЕГЭ по русскому языку)', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/54849/cover_k4C2WE1/84b9ab68c9e90de9d5b640ead0c28b18.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (54823, 1, 'Мусор1', true, '<p>Благодаря этому курсу вы научитесь использовать популярные инструменты профессиональной оценки персонала. </p><p>Курс содержит продуманный набор практических заданий, позволяющий воспринимать информацию не пассивно, а погружаться и размышлять над каждой темой. Мы изучим популярную типологию личности, оценку "360 градусов" и такие методы оценки как тестирование, интервью по компетенциям, case-study.</p><p>Зачем проходить этот курс?</p><p>1. провести самооценку, чтобы выявить свои слабые и сильные стороны и использовать эту информацию для своего развития и карьеры;</p><p>2. научиться оценивать других людей: коллег, подчиненных, кандидатов на работу, фрилансеров на проект;</p><p>3. если вы увлекаетесь психологией и психодиагностикой.</p><p><br></p><p><br></p>', '{101260529}', 'self_paced', 1078, 23, 2360, 'ru', 'Широкий круг интересующихся психологией и психодиагностикой', true, 4.6, 'Оценка персонала и самодиагностика. Базовый курс', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/54823/cover_672ehl8/16e4d1d941f0867cd3e782218bf09a3b.png');
