INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (51541, 13, 'Мусор2', false, 'Курс посвящен основам продаж на базе прямолинейной системы продаж Д. Белфорта. <br>Из курса вы узнаете как продавать с помощью НЛП-технологий: Интонации, речевые паттерны, технологии закрытия возражений', '{53083009}', 'self_paced', 564, 63, 22666, 'ru', 'NA', false, 4.6, 'Описание программы "Прямолинейной системы продаж"', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/51541/cover_HQa0kC0/10a2dcc3b69a86f7b8af9e51fd47e4ba.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (53196, 12, 'Мне повезет', false, '<p>Задачи курса: </p>

<p>дать краткую
характеристику периода «оттепели»;</p>

<p>представить
поэтов- «шестидесятников», их жизненный и творческий путь; </p>

<p>выявить основные
направления в творчестве поэтов периода « оттепели».</p>



<p>Онлайн-курс "Поэзия "Оттепели" рассчитан на  учащихся образовательных<br>организаций среднего профессионального образования и средних школ, а также всех заинтересованных читателей и ценителей русской литературы.  Курс построен<br>с учетом Федерального государственного образовательного стандарта среднего<br>общего образования, отражает основные веяния эпохи и направления развития поэзии 60-х годов.  <br></p><p>Курс призван
помочь учащимся успешно освоить учебную программу по литературе в рамках внедрения и апробации дистанционного обучения.</p>

<p>Уроки курса
включают в себя лекционные материалы, иллюстративный материал, видеоролики,
