INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (52892, 5, 'Введение в основы программирования и анализ данных', false, '<p><em>﻿ </em>Курс ориентирован на начинающих программистов, и на тех, кто интересуется программированием. Данный курс является ознакомительным. В нем доступным языком описываются базовые принципы, на которых построено программное обеспечение, инструменты и подходы к организации процесса разработки приложений.<br>
