INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (46792, 11, 'Школьные курсы и егэ по математике', false, '<div>Курс представляет собой набор задач по элементам теории алгоритмов из ЕГЭ по информатике, созданный на основе <a href="http://www.fipi.ru/content/otkrytyy-bank-zadaniy-ege" rel="nofollow noopener noreferrer" title="Link: http://www.fipi.ru/content/otkrytyy-bank-zadaniy-ege">открытого банка заданий ЕГЭ «Федерального института педагогических измерений»</a> 2018 года. Все материалы абсолютно бесплатны.

