INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (46775, 17, 'Обучение, управление (кластер не очень)', false, '<p>Данный курс в интерактивной форме знакомит студентов с тем, как выглядели известные философы.</p><p><b>Особенности курса: </b></p><p>Курс является адаптивным: система учитывает число попыток, время, потраченное на выполнение заданий, как часто задания назывались студентами слишком легкими или слишком простыми. Адаптивная система работает динамически, постоянно совершенствуясь на основе поведения пользователей, пересчитывая сложности задач и уровень знаний пользователей.</p><p>Научный консультант Никоненко С.В., ﻿﻿﻿профессор, д.филос.н. ﻿<br></p><p><b>Источники: </b></p><p>1. История философии/ Учебник под.ред. В.В.Васильева, А.А.Кротова, Д.В.Бугая.</p><p>2. Философская антропология. Очерки истории и теории, Б.В.Марков. </p><p>3. Философия. Учебник, Б.В.Марков. </p><p>4. Изображения взяты с сайта <a href="http://www.wikipedia.org" rel="nofollow noopener noreferrer">www.wikipedia.org</a></p><p>﻿Автор логотипа <a href="https://vk.com/awwtmnart" rel="nofollow noopener noreferrer" title="Link: https://vk.com/awwtmnart">Евгения Ляпина</a><br></p>', '{31135261}', 'self_paced', 277, 300, 3952, 'ru', 'Курс подойдет каждому', false, 4.2, 'Узнай философа', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/46775/cover/4e4380e362196cef3c6b469261b2faf4.png');
