INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (46736, 3, 'Огэ, егэ (без математики)', false, '<div>Курс представляет собой набор задач по системам счисления из ЕГЭ по информатике, созданный на основе <a href="http://www.fipi.ru/content/otkrytyy-bank-zadaniy-ege" rel="nofollow noopener noreferrer" title="Link: http://www.fipi.ru/content/otkrytyy-bank-zadaniy-ege">открытого банка заданий ЕГЭ «Федерального института педагогических измерений»</a> 2018 года. Все материалы абсолютно бесплатны.

</div><div><br></div><div>В курсе нет разборов задач, есть только их автоматическая проверка.</div><div><br></div><div>

В начале обучения тренажер сам предложит вам первую задачу, а для каждой новой задачи он будет учитывать ваш уровень знаний. Помогайте ему: если задача для вас слишком лёгкая или слишком сложная, нажмите соответствующую кнопку над ней. </div><div><br></div><div>А ещё вы можете пройти этот тренажёр в мобильном приложении (оно тоже бесплатно): для <a href="https://play.google.com/store/apps/details?id=org.stepik.android.adaptive.ege8290" rel="nofollow noopener noreferrer" title="Link: https://play.google.com/store/apps/details?id=org.stepik.android.adaptive.ege8290">Android</a> и <a href="https://itunes.apple.com/ru/app/%D0%B5%D0%B3%D1%8D-2018-%D0%B8%D0%BD%D1%84%D0%BE%D1%80%D0%BC%D0%B0%D1%82%D0%B8%D0%BA%D0%B0-%D1%8D%D0%BA%D0%B7%D0%B0%D0%BC%D0%B5%D0%BD/id1374421802?mt=8" rel="nofollow noopener noreferrer">iOS</a>. </div><div><br></div><div>Удачи!</div>', '{60445783}', 'self_paced', 19738, 65, 9858, 'ru', 'NA', false, 4.85714285714286, 'ЕГЭ Информатика 2018: Системы счисления', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/8290/cover/76f50920d263a7727775fa869c3703c4.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (46675, 13, 'Мусор2', false, 'Ass', '{45422581}', 'self_paced', 118, 25, 2989, 'ru', '8-9 классы общеобразовательных  учреждений, гимназий, профильных гуманитарных классов', false, 5, 'Словосочетание', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (46671, 13, 'Мусор2', false, '<p>В курсе рассмотрены правила соревнований Всемирного союза киокусинкай (KWU). Курс предназначен для судей KWU и спортсменов. <br></p>', '{59771454}', 'self_paced', 168, 151, 4758, 'ru', 'судьи KWU', false, 5, 'Правила соревнований KWU (кумитэ)', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/46671/cover/09e92b6f6d48abd6778b67b886c3fdad.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (46451, 2, 'Математический анализ, статистика', false, 'Ass', '{113661}', 'self_paced', 258, 64, 6169, 'ru', 'NA', false, 4, 'Математика СД-1', 'От 4 дней до полутора недель', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (38218, 7, 'Программирование и анализ данных для продолжающих', true, '<p><span>На этом курсе ты узнаешь как работает интернет, научишься создавать сайты с помощью HTML и CSS и размещать их в сети. Попутно освоишь основные инструменты веб-разработчика: редактор кода, отладчик, Google.</span><br></p><p>Не бойся страшных непонятных слов! Курс создан специально для людей, которые никогда не программировали и ничего не знают о работе интернета.</p><p>Этот курс - инкарнация классического оффлайнового курса от <a href="https://vk.com/reu_itc" rel="nofollow noopener noreferrer">ITC</a>, который проводится каждый год в <a href="https://rea.ru/" rel="nofollow noopener noreferrer">РЭУ им. Плеханова</a> для всех желающих студентов бесплатно. <span>Мы решили разместить материалы курса на Степике. Он содержит все материалы и задания, которые мы даём нашим студентам.</span></p>', '{24073100}', 'self_paced', 74249, 37, 9843, 'ru', 'NA', false, 4.87341772151899, 'Веб-разработка для начинающих: HTML и CSS', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/38218/cover_S6lejZQ/076bc6bbbf904b46faca1403eac8d833.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (36403, 0, 'Языки', false, '<p>Данный курс является ВТОРЫМ в цикле моих курсов польского языка (первая часть: <a href="https://stepik.org/course/24913" rel="nofollow noopener noreferrer">https://stepik.org/course/24913</a>) и содержит в себе все необходимые материалы для самостоятельного обучения употреблению польских глаголов в разных временах, а также наборы слов и выражений для накопления словарного запаса.</p>

<p>Подходит для продолжения обучения  всем, кто умеет читать по-польски и знает базовые принципы грамматики. ﻿Также благодаря возможностям платформы Stepik, во время обучения возможна коммуникация с преподавателем, есть функции, позволяющие оценивать работы студента (выполнение заданий), комментирование (обратная связь) и прочее.</p>

<p>ВНИМАНИЕ! Целью курса не является объяснение ВСЕГО ПОЛЬСКОГО за максимально краткие сроки.</p>

<p>В процессе обучения есть возможность получить уровень А2 знания языка.</p>

<p>Курс включает в себя: простые объяснения основных правил (для самостоятельного обучения), принципов грамматики, а также мультфильмы, песни, лексические наборы и короткие задания для самопроверки.</p>

<p>Учебная программа составлена согласно рекомендациям Министерства Образования Польши для изучения польского языка как иностранного, взятым из открытых официальных источников.</p>
', '{48013463}', 'self_paced', 677, 155, 21154, 'ru', 'Подходит школьникам, студентам, работающим людям, пенсионерам. 

Всем тем, кто интересуется польским языком для работы /учёбы /путешествий /хобби и готов работать над дальнейшим его изучением.', false, 5, 'Польский язык. Часть 2 (А2). Czas na czasownik', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/36403/cover/a0896a43eb2c5d233e62ef3667e3f1a8.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (33397, 15, 'Курсы на английском языке по разным тематикам', false, '<p>Если вы смотрите видео и читаете тексты этого курса, вероятно вы были избраны на роль одного из менторов Coursera.<br><br>О том <b>КТО </b>такие менторы, <b>ЗАЧЕМ </b>они нужны и <b>ЧТО </b>они делают и будет этот курс.<br></p><p>Так же в дополнительным материалах будет некоторое количество информации о том как работают те или иные инструменты курсеры и даны полезные советы (например, как можно сэкономить денег на обучении).</p>', '{17443953}', 'self_paced', 157, 54, 5887, 'ru', 'NA', false, 5, 'Coursera Mentor Guide', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/33397/cover/1184ff6cea42492d33f68ac8ed75fa72.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (32065, 17, 'Обучение, управление (кластер не очень)', false, 'Ass', '{28414308}', 'active', 228, 172, 13662, 'ru', 'NA', false, 5, 'Самостоятельная работа в курсе "Общая экология"', 'От 4 дней до полутора недель', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (29755, 12, 'Мне повезет', true, '<p>Курс "Теория физической культуры" предназначен для студентов разных направлений бакалавриата, изучающих дисциплину "Физическая культура". Материалы данного курса будут полезны и всем интересующимся: они дают возможность получить представления об основах здорового образа жизни и лечебной физической культуре как средстве профилактики и реабилитации при различных заболеваниях.</p><p>Курс нацелен на формирование интереса к занятиям физической культурой и спортом, культуры здорового образа жизни, укрепление здоровья.</p>', '{38793712}', 'self_paced', 15373, 155, 21502, 'ru', 'Студенты бакалавриата всех направлений подготовки за исключением направления "Физическая культура", а также все интересующиеся.', false, 4.10714285714286, 'Теория физической культуры', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/29755/cover_Uv1xz24/75a52d317b079fc8ccfb4dd45ed28eea.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (31893, 16, 'НКО (некоммерческие организации)', true, '<p>Курс позволит участникам получить с нуля или улучшить свои навыки написания современных социальных проектов (в том числе для получения финансирования) и управления их реализацией.</p>

<p>На курсе вы сможете:</p>

<p>* обобщить свой опыт и структурировать знания по подготовке социально значимых проектов, их реализации и отчетности;</p>

<p>* познакомиться с опытом проектного менеджмента коллег из НКО разных регионов России, завязать полезные контакты;</p>

<p>* узнать о современных концепциях и инструментах проектного менеджмента (таких, как Scrum, Agile и др.), оценить их полезность для своей организации;</p>

<p>* разработать свой собственный проект;</p>

<p>* спланировать применение новых знаний, завершить курс с сертификатом и с готовыми предложениями для своей команды.</p>

<p>Формат обучения предполагает самостоятельное изучение вами предложенных на платформе видеолекций, текстовых материалов и выполнение заданий – в удобное время. Есть формат для компьютера и мобильных устройств, можно проходить курс с телефона.</p>

<p><strong>Программа курса:</strong></p>

<p><strong>Модуль 1. Что такое социальный проект</strong></p>

<p>Модуль 2. Улучшаем свой проект: проектные подходы</p>

<p>Модуль 3. Заявка на грант. Бюджет проекта</p>

<p>Модуль 4. Управление проектом на этапе реализации</p>

<p>Модуль 5. Оценка проекта. Отчетность</p>

<p>Проект реализуется МБОО «Центр РНО» с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>
', '{28878375}', 'self_paced', 4085, 35, 46277, 'ru', 'Курс будет полезен проектным менеджерам, руководителям, фандрайзерам зарегистрированных НКО – то есть тем, кто активно участвует в создании проектов и координирует их реализацию. Особое приглашение на курс - тем, кто живет на отдаленных от крупных городов территориях.', false, 5, 'Социальное проектирование в НКО', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/31893/cover/4cc7a2b3cb22dd7c7377389d11200ac4.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (31303, 17, 'Обучение, управление (кластер не очень)', false, '<p>Курс разработан для студентов, обучающихся по специальности высшего образования – специалитет 33.05.01 Фармация, а также работников аптечных организаций (провизоров, фармацевтов, работающих за "первым столом") с целью повышения доступности рациональной фармакотерапии и снижении нагрузки на лечебные учреждения. Данный курс состоит из девяти занятий, одно из которых посвящено роли провизора и фармацевта в системе здравоохранения. В рамках восьми тем изучаются алгоритмы фармацевтического информирования и консультирования пациентов разных возрастных групп, по таким наиболее частым обращениям к провизору за помощью, как боль, заболевания ЖКТ, сердца и т.д., анализируются опасные симптомы и синдромы, требующие срочного обращения к врачу.</p>

<p>Создатели курса:</p>

<p>Оковитый Сергей Вламирович,заведующий кафедрой фармакологии и клинической фармакологии ФГБОУ ВО СПХФУ Минздрава России,</p>

<p>Зима Елена Алексеевна, советник при ректорате, и.о. начальника отдела подготовки кадров высшей квалификации ФГБОУ Во СПХФу Минздрава России,</p>

<p>Ильинова Юлия Геннадьевна, проректор по учебной работе ФГБОУ ВО СПХФУ Минздрава России,</p>

<p>Титович Ирина Александровна, научный сотрудник центра экспериментальной фармакологии, ассистент кафедры фармакологии и клинической фармакологии ФГБОУ ВО СПХФУ Минздрава России</p>
', '{53919118}', 'self_paced', 1053, 54, 14958, 'ru', 'Работники аптечных организаций - провизоры, провизоры – технологи, фармацевты', false, 4.63636363636364, 'Аспекты рационального применения лекарственных средств', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/31303/cover_wm2gA9D/3f2d2cc7a7e28af0c0633ed929e998d9.jpeg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (31182, 5, 'Введение в основы программирования и анализ данных', false, '<p>Курс рассчитан на людей, не имеющих опыта в программировании, и призван обучить учащихся основам языка Python.</p>

<p>Курс находится в процессе разработки: на данный момент доступны модули "Основные конструкции", "Функции", "Структуры данных" и "Модули", состоящие из 19 уроков. Всего планируется несколько модулей с общим объемом в 25-30 уроков:</p>

<ol>
	<li>Основные конструкции языка (включает в себя ввод и вывод информации, условный оператор, циклы, основные типы данных).</li>
	<li>Функции (стандартные и пользовательские функции, генераторы и сортировки).</li>
	<li>Структуры данных (словарь, множество, линейные структуры данных)</li>
	<li>Стандартные и внешние библиотеки.</li>
	<li>Парадигмы программирования (введение в функциональное программирование и ООП).</li>
	<li>API. Разработка телеграм-бота.</li>
</ol>

<p>Каждый урок, освещающий новую тему, состоит из подробной текстовой лекции, после которой идет 10-15 задач на отработку этой темы. Любые вопросы по материалам курса, а также по содержанию задач можно задавать в комментариях.</p>
', '{37659602}', 'active', 7993, 34, 188344, 'ru', 'Школьники 7-8 классов, а также школьники и студенты другого возраста, интересующиеся программированием.', false, 5, 'Введение в Python (7-8 классы)', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/31182/cover/746769a780f102ed4df5f093724a2eaa.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (30576, 4, 'Школьные предметы', false, '<p><i>Для прохождения тренажёра необходимо зарегистрироваться на платформе и нажать кнопку "Поступить на курс".</i></p><p><b>Цель курса-тренажёра - отработка и закрепление основных вычислительных навыков, необходимых для успешной сдачи ОГЭ по математике в 9 классе.</b></p><p>В основе тренажёра лежат задачи из открытого банка заданий ОГЭ ФИПИ (для каждой задачи указан соответствующий номер), проработанные автором и систематизированные по темам  "Дроби", "Степень", "Корень", "Раскрытие скобок". </p><p>В каждой теме есть уроки, содержащие пример решения типового задания и 10 задач для тренировки. <b>Всего в тренажёре 500 задач.</b></p><p>Тренажёр может использоваться учениками 7-11 классов и учителями математики в качестве онлайн-задачника.</p><p><br></p><p><br></p><p><br></p>', '{38052761}', 'self_paced', 228, 500, 15968, 'ru', 'Школьники (7-11 классы), учителя математики', false, 5, 'Числа и вычисления. Задачник-тренажёр ОГЭ.', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/30576/cover_qCUbnah/53cb79bfe52f787836dc73cbe41a2ef8.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (29754, 13, 'Мусор2', false, '<p>Сегодня от выпускников образовательных учреждений требуется быстрое вхождение в социум, что предполагают обладание ими способностями принимать решения по обеспечению своей жизнедеятельности, готовности и умения реализовать принимаемые решения. Однако, многие выпускники, в частности выпускники детских домов, не вполне готовы к самостоятельному жизнеустройству как в бытовом, так и социальном отношении. В настоящее время одним из актуальных направлений подготовки воспитанников детских домов к самостоятельной жизни является финансовое просвещение.<br></p><p>В этой связи Благотворительным фондом «АК БАРС СОЗИДАНИЕ» разработан курс «Благопредприниматель» для воспитанников детских домов Приволжского федерального округа по финансовой грамотности, направленный на создание условий для обучения детей-сирот из специализированных образовательных учреждений ПФО основам экономической безопасности, повышение их мотивации к изучению и применению на практике финансово-экономических решений. <br></p>', '{36096534}', 'self_paced', 432, 29, 10587, 'ru', 'Воспитанники специализированных образовательных учреждений для детей-сирот и детей, оставшихся без попечения родителей, Приволжского федерального округа', false, 4.95, 'Благопредприниматель', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/29754/cover_hDFEpwz/f920595b551f75791e008bd5e61523ad.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (29577, 17, 'Обучение, управление (кластер не очень)', false, '<p><b>В рамках курса «Методы планирования эксперимента» рассмотрены принципы построения основных планов эксперимента: полного факторного эксперимента, дробного факторного эксперимента, композиционные планы, включая ортогональные и ротатабельные планы.</b><br></p>', '{58087451}', 'self_paced', 261, 73, 8227, 'ru', 'NA', false, 5, 'Методы планирования эксперимента', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/29577/cover_ubf8P2E/11496f716c65fcf52575f4f32cc1f1a4.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (29467, 17, 'Обучение, управление (кластер не очень)', false, '

<div>Кейс-технологии являются наиболее перспективным направлением подготовки инженерных кадров, так как позволяют обучающимся успешно применить полученные теоретические знания в процессе решения практических задач.</div><div><br></div><div>Наш курс разработан с учетом требований, предъявляемых к участникам команд, решающих инженерные кейсы. Вы познакомитесь с основными правилами кейсов, узнаете, как собрать лучшую команду, качественно решить кейс, оформить презентацию и хорошо выступить на кейс-чемпионате.</div><div><br></div><div>Авторы курса расскажут об особенностях этого процесса и поделятся профессиональными секретами, что поможет вам удачно избежать многих подводных камней.</div><div><br></div><div>Изучите наш курс, соберите эффективную команду и боритесь за победу в кейс-чемпионате!</div>

<p><span style="font-size: 1em; background-color: transparent;"> </span></p>', '{52162639}', 'self_paced', 194, 40, 9586, 'ru', 'Участники кейс-чемпионатов', false, 5, 'Решение инженерных кейсов', 'От 4 дней до полутора недель', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (28995, 1, 'Мусор1', false, '<p>  Курс представляет собой аккумулированный за пять лет опыт работы с 3D-принтерами, работающими по технологии FDM (Fused Deposition Modeling), т. е. моделирование методом осаждения расплавленной нити.<br>  Собрав немалый пул распечатанных моделей разной геометрии, мы, наставники курса - <b>Востров Никита и Бабайцев Михаил</b>, решили поделиться своими наработками, а также возникшими на нашем пути проблемами, т. к. последние многому нас научили.</p><p>  <b>Почему именно в формате онлайн-курса, а не классического набора статей?</b> - Отличный вопрос! Тому есть 2 основные причины:<br>  <b>Первая</b> - <b>мы нацелены на создание образовательной системы в области FDM-печати,</b> чем занимаемся на протяжении последних лет. Наша система должна иметь обратную связь и проверку приобретаемых знаний, поэтому онлайн-курсы для дистанционного обучения - идеальный вариант;<br><b> Вторая  - мы активные популяризаторы перспективных технологий </b>среди молодежи и взрослых,<b> путем наглядной демонстрации!<br></b><br>  Если ты разделяешь наши ценности и стремления, <b>WELCOME</b>!<br><br>  По <b>результатам</b> прохождения курса "Искусство аддитивных технологий":</p><ol><li>У тебя появится объективное понимание технологии FDM-печати;<br></li><li>Познакомишься с различными материалами, применяемыми для FDM-печати;<br></li><li>Научишься обрабатывать напечатанные модели и превращать их в законченные изделия;<br></li><li>Узнаешь в каких областях применяется 3D-печать;<br></li><li>При желании, реализуешь два абсолютно разных проекта;<br></li><li>Увлечешься 3D-печатью и включишься в общее сообщество 3D-мейкеров!</li></ol><p><i></i><b>  P.S. </b><b>Для наилучшего погружения в тематику курса, мы подготовили для тебя программы в формате виртуальной реальности!</b><i><b><br></b><br></i>  Заинтересовался? Готов погрузиться в мир 3D-печати? <b>Тогда нажимай кнопку "Записаться на курс" и делай свой первый шаг в "НАРНИЮ" </b><b>научно-технического творчества</b><b>!</b><i><br></i></p>', '{19044123,57988076}', 'self_paced', 843, 107, 15251, 'ru', 'Курс преимущественно рассчитан на аудиторию от 12 лет до 18 лет (6 - 11 класс), а также будет полезен и для взрослой аудитории, кто только начинает знакомство с 3D-печатью.', false, 4.14285714285714, 'Искусство аддитивных технологий (3D-печать/3D-printing)', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/28995/cover_6L9gYtz/2e239751fc25a15bac56c35a92068e03.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (28608, 14, 'Мусор3', false, '<p>Решили организовать<b> Тотальный диктант</b>, но не знаете, с чего начать? Хотите <b>стать координатором </b>проекта в своём городе, но не понимаете, как это сделать? Или вы уже <b>не первый год координируете </b>Тотальный диктант в своём населённом пункте, но не перестаёте совершенствовать свои навыки? Тогда этот курс для вас! <b><br></b></p><p><b>#спасибочтоделишься</b> —  курс, подготовленный членами Штаба и Экспертного совета Тотального диктанта для каждого, кто решил провести диктант в своём городе и подарить людям настоящий праздник грамотности.</p><p>Вас ждут базовые лекции о <b>принципах</b>, <b>истории</b>, <b>организационных моментах </b>и <b>структуре</b> проекта, после которых вы с уверенностью сможете сказать, что <i>организуете диктант на все сто процентов</i>! Но и это еще не всё: мы попросили наших лекторов — <i>координаторов из разных городов мира </i>— поделиться своим опытом, представить свои кейсы, которые посвящены разным аспектам организации Тотального диктанта в их городах: работе с партнёрами, церемонии награждения, координации нескольких площадок и так далее.</p><p>Если вы <b>участник </b>Тотального диктанта, то не раскрывайте все секреты проекта: оставьте координаторам поле для манёвров :) но если вам интересно узнать, как все проходит, записывайтесь на курс!</p><p><i>Курс создан с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</i></p>', '{26456792}', 'self_paced', 455, 89, 35885, 'ru', 'координаторы Тотального диктанта, члены городских команд Тотального диктанта', false, 4.85, '#спасибочтоделишься: курс для координаторов Тотального диктанта', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/28608/cover/433e72196fe532363dd73ae9a9d80d33.jpeg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (27933, 17, 'Обучение, управление (кластер не очень)', false, '<p>Данный марафон нацелен на создание видимых изменений в вашем профиле.<br>Темы марафона<br>- оформление профиля<br>- виды контента<br>- настройка трафика<br>- настройка таргета<br>- на чем заработать в инстаграм <br></p>', '{57578558}', 'self_paced', 6991, 11, 3711, 'ru', 'NA', false, 3.85, 'Инстаграм марафон', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/27933/cover_I7LFTgK/b38d334da5543a878de8beec2062807e.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (27475, 12, 'Мне повезет', false, '<p>

Курс для школьников 8-12 лет, направленный на формирование личностных качеств, необходимых для добровольческой (волонтерской) деятельности: доброты, отзывчивости, стремления помочь людям, желания лично изменить что-то в этом мире к лучшему, стойкости, целеустремленности.</p><p>Направления курса: «Помогаем младшим»; «Добровольцы-экологи»; 

«Братья наши меньшие»;

 «Помощь особым людям»; «Добровольцы-краеведы»; «Художественное творчество»; «Журналистика».</p><p>Курс нацелен на то, чтобы дети захотели творить добро, с радостью и вдохновением, не задумываясь о том, какую им это принесет выгоду. Это и есть основа добровольчества. Такой подход - основа будущей деятельности в некоммерческой сфере. Но если участники нашего курса станут чиновниками или бизнесменами - они будут человечными и отзывчивыми чиновниками и бизнесменами. А нам этого сегодня так не хватает!</p><p>Курс создан с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.

<br></p>', '{43871113}', 'self_paced', 125, 33, 4624, 'ru', 'Школьники 8-12 лет, их родные и друзья, педагоги', false, 5, 'Школа юных добровольцев', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (25439, 2, 'Математический анализ, статистика', true, '<p><em></em></p>При прохождении курса вам будет предложен просмотр видео по <i>теории</i> и разбору <i>типовых задач </i>и<i> набор тестов с автоматической проверкой</i> для применения полученных знаний<i></i>. <div><br></div><div>При наличии времени и желания можете порешать задачи из раздела "Дополнительные материалы" и воспользоваться интернет-ссылками и литературой из рекомендованного списка. <div><br></div><div>При возникновении вопросов по условиям и оформлению ответов задавайте их на форуме или пишите организаторам по электронной почте. </div><div><br></div><div>Напоминаем, что делать подсказки в комментариях и тем более выкладывать ответы запрещено. <br>В конце курса вас ждёт <i>итоговый тест</i>.<br><br>Решение задач на курсе возможно на <b>нескольких уровнях</b>.<br><br><ol><li><b>Программа-минимум</b> - решение тестов. В этом случае достаточно получить верный ответ к задаче.</li><li><b>Второй уровень</b> - решение задач из раздела "Дополнительные материалы". Здесь уже потребуется обоснование, кроме того, автоматическая проверка в этом случае не предусмотрена. Решения задач этого раздела, как правило, довольно короткие.</li><li><b>Третий уровень</b> - решение самостоятельно выбранных вами задач из рекомендованных нами источников, причём большинство задач там сложнее, чем в разделе "Дополнительные материалы".</li><li>Наконец, <b>четвёртый уровень</b> - решение задач из сравнительно сложных математических олимпиад, которые вы тоже можете найти по нашим ссылкам.</li></ol>Обсуждение творческих предложений и найденных вами образовательных траекторий тоже можно проводить в комментариях к курсу.<p><em></em></p></div></div>', '{56390506}', 'self_paced', 7198, 181, 70147, 'ru', 'Старшеклассники, преподаватели математики.', false, 4.5, 'Задачи повышенной сложности по математике для старшеклассников', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/25439/cover/7af65eff8f71126af4f5485d9b3a8ebe.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (25006, 13, 'Мусор2', false, '<p>Согласно статье 225 ТК РФ все работники, в том числе руководители организаций, а также работодатели - индивидуальные предприниматели, обязаны проходить обучение по охране труда и проверку знания требований охраны труда в порядке, установленном уполномоченным Правительством Российской Федерации федеральным органом исполнительной власти с учетом мнения Российской трехсторонней комиссии по регулированию социально-трудовых отношений.</p><p>Поэтому, благодаря этому курсу, вы сможете пройти обучение по охране труда и проверку знаний требований охраны труда дистанционно.</p>', '{56113202}', 'self_paced', 73, 37, 2069, 'ru', 'Специалисты и прочий персонал  БУ "Ханты-Мансийский клинический кожно-венерологический диспансер"', false, 5, 'Программа обучения и проверки знаний по охране труда', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (24913, 1, 'Мусор1', true, '<p>Данный курс является базовым в цикле моих курсов и содержит в себе все необходимые материалы для самостоятельного обучения чтению, а также основные принципы польской грамматики и наборы слов и выражений для накопления словарного запаса.</p>

<p>Подходит <em><strong>для начала обучения</strong></em> абсолютно всем, так как в курсе представлены основы, т.н. "азы". Также благодаря возможностям платформы Stepik, <em><strong>во время обучения возможна коммуникация с преподавательницей</strong></em>, есть функции, позволяющие оценивать работы студентов (-ок) (выполнение заданий), комментирование (обратная связь) и прочее.</p>

<p>Также <em><strong>подходит в качестве ознакомительного курса</strong></em>, если Вы ещё до конца не определились, хотите ли Вы вообще учить польский язык. Данный курс поможет Вам самостоятельно оценить объём и сложность предполагаемой работы для Вас лично.</p>

<p><em><strong>ВНИМАНИЕ! Целью курса не является объяснение ВСЕГО ПОЛЬСКОГО за максимально краткие сроки. Курс содержит вводную информацию для дальнейшего продолжения обучения.</strong></em></p>

<p>В процессе обучения есть возможность получить уровень знаний, соответствующий критериям <em><strong>А1</strong></em> (знания польского языка как иностранного).</p>

<p><em><strong>Курс включает в себя:</strong></em> простые объяснения основных фонем (для самостоятельного обучения чтению), базовых принципов грамматики, а также мультфильмы, песни, лексические наборы и короткие задания для самопроверки.</p>

<p><em><strong>Учебная программа составлена</strong></em> согласно рекомендациям Министерства Образования Польши для изучения польского языка как иностранного, взятым из открытых официальных источников.</p>

<h3>Как учиться на этом курсе?</h3>

<p>Благодаря платформе Stepik, курс полностью автоматизирован.</p>

<p><em><strong>При прохождении</strong></em> шагов / уроков / модулей <em><strong>Вы увидите</strong></em> от меня <em><strong>указания</strong></em> и советы, как правильно заниматься в следующем.</p>

<p><em><strong>Курс содержит</strong></em> разные <em><strong>задания</strong></em>, в основном тестовые, правильность выполнения которых проверяется автоматически.</p>

<p><em><strong>Связь с</strong></em>о мной, Вашей <em><strong>преподавательницей</strong></em>, Вы можете поддерживать непосредственно здесь, на платформе (задавая вопросы в комментариях) или в соцсети Фэйсбук (ссылка будет в курсе). Если возникнет необходимость, могу дать другие свои контакты.</p>

<p>Програма курса включает в себя также <em><strong>модули самопроверки</strong></em>, где Вы сможете объективно оценить свои достижения.</p>

<p>В общем, <em><strong>пробуйте, начинайте</strong></em>, а если вдруг что-то пойдёт не так - <em><strong>я</strong></em> Вам <em><strong>помогу</strong></em>))</p>', '{}', 'self_paced', 3597, 55, 9295, 'ru', 'Подходит школьникам (-цам), студентам (-кам), работающим людям, пенсионерам (-кам). Также тем у кого "плохо с языками", кто "давно закончил школу и ничего не помнит", кто "необучаем" и тд.', false, 4.89473684210526, 'Польский язык. Основы (А1). Быстрое обучение чтению', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/24913/cover_1jgPyg5/c91c5c2d3ba616233a26097aab5d04d0.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (24660, 1, 'Мусор1', false, '<p>Gain a wide view of the physics of light interaction with metal nanostructures. In this course, you will learn about the whole diversity of unique effects appearing at the junction of nanotechnology, subwavelength optics, quantum mechanics, and solid-state physics. You will find out how giant field enhancement near metallic nanostructures can be used for detecting single biomolecule, and whether it is possible to build a nanometer-scale laser.</p>

<p>The course is taught in <a href="https://physics.ifmo.ru/index.php/en/study_master" rel="noopener noreferrer nofollow">Nanophotonics and Metamaterials Master program</a> at the Faculty of Physics and Engineering, ITMO University. </p>

<p> </p>
', '{2115459,15662177}', 'active', 136, 115, 38015, 'en', 'This course is aimed at graduate and undergraduate students who are majoring in physics and engineering science related to optics. As well as researchers who want to gain or deepen their knowledge in the field of modern photonics. This course can give a boost to your educational or academic career, and potentially will stimulate you to conduct your own research in this field.', false, 5, 'Introduction to Plasmonics', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/24660/cover_StK7MO2/118da83e34097d401d95139a46621689.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (23357, 1, 'Мусор1', false, '<p>Основная цель курса: дополнить инженерные компетенции слушателей видением технологического предпринимательства глазами бизнеса, рынка и клиента. В курсе будет рассказано, что нужно для того, чтобы проект состоялся не только с технической точки зрения, но и с точки зрения бизнеса. Полученная информация поможет понять: из чего состоит проект, как собрать необходимые ресурсы, команду и как продвигать проект дальше.<br></p>', '{43769607}', 'self_paced', 770, 90, 10687, 'ru', 'NA', false, 4.8, 'Основы технологического предпринимательства', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/23357/cover_WxrxUXk/4dc64d5b70c5acf374fcd21b8fe829da.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (24303, 1, 'Мусор1', false, '<p>Курс для детей младшего и среднего школьного возраста на основе занятий с профессиональным шахматистом, тренером на Фоксфорде (<a href="http://foxford.ru" rel="noopener noreferrer nofollow">foxford.ru</a>). В курсе даются основы для самостоятельного построения выигрышных стратегий в игре шахматы, дается разбор ошибочных действий в дебюте, миттельшпиле, эндшпиле; с помощью тестовых заданий можно проверить свои знания.</p>

<p>Больше учебных материалов, курсов (в том числе подсказок и ответов на некоторые задания этого курса) ищите на сайте <a href="http://msolkin.ru/" rel="nofollow noopener noreferrer">msolkin.ru</a></p>

<p>Также подписывайтесь на канал яндекс.дзена "<a href="https://zen.yandex.ru/chessnok" rel="noopener noreferrer nofollow">Chess''n''ok</a>", где буду публиковать интересные задачки на постановку мата в 1-10 ходов, на выигрыш материала, на лучшие ходы. <a href="https://zen.yandex.ru/id/5cfd06fbe77f2e00b01cf7e3" rel="noopener noreferrer nofollow">Канал </a>только развивается, так что помогайте!</p>
', '{29417050}', 'self_paced', 2358, 163, 63540, 'ru', 'школьники 1-8 классов', false, 4.33333333333333, 'Шахматы. Основы для начинающих', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/24303/cover/dada8af93e273a8484b516be8e1077fd.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (24055, 12, 'Мне повезет', false, '<p>Школа зооволонтёра - это собранные вместе базовые знания, необходимые для грамотной помощи животным в приютах и не только. Опытные волонтёры с помощью этого курса смогут проверить и повысить свой уровень знаний. Начинающим же волонтёрам и тем, кто только собирается ими стать, курс поможет избежать опасных ошибок и с самого начала помогать животным грамотно. <br>В Школе в данный момент открыты четыре модуля: <br>1. "Краткий курс психологии кошек" рекомендован всем, кто работает с кошками или хотел бы им помогать, а также он будет интересен владельцам кошек. Лектор модуля - опытный зоопсихолог, фелинолог, практикующий консультант по поведению кошек Татьяна Куликова. <br>2. "Основы кинологии" мы рекомендуем всем, кто занимается помощью собакам: опытным и потенциальным волонтёрам приютов, частным кураторам, а также владельцам собак. Лектор модуля - Ольга Орлова, кинолог с огромным опытом работы с проблемными собаками, зоопсихолог, практикующий консультант.<br>3. Модуль "Основы ветеринарии" подготовлен совместно с ветеринарной клиникой "Биоконтроль", лекторы модуля - действующие ветеринарные врачи разных специализаций, признанные профессионалы в своих областях. Модуль рекомендован всем волонтёрам и хозяевам животных для получения базовых знаний о содержании, уходе и своевременном оказании грамотной помощи животным. <br>4. Модуль "Животные и здоровье людей" подготовлен совместно с сетью медицинских клиник "Медси" и главным врачом Татьяной Вячеславовной Семенычевой, аллергологом-иммунологом с 25-летним стажем работы. Модуль рекомендован всем заинтересованным в создании благоприятной среды для совместной жизни животных и людей. <br>Приглашаем всех в Школу зооволонтёра! Учитесь помогать животным правильно, ведь они полностью зависят от нас!</p>', '{42726779}', 'self_paced', 3091, 60, 30885, 'ru', 'Волонтёры частных и муниципальных приютов для животных, волонтёрских групп и частные кураторы, а также те, кто хотел бы стать волонтёром и помогать животным. 
Курс будет интересен и владельцам кошек и собак, которые хотели бы узнать больше о грамотном содержании своих питомцев.', false, 4.9, 'Школа зооволонтёра', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/24055/cover_nD2oqlY/feb0b1c385e75bc99cb4247aa9571cb0.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (23544, 7, 'Программирование и анализ данных для продолжающих', false, 'Ass', '{17556297}', 'active', 382, 12, 50459, 'en', 'NA', false, 4.75, 'Problem Solving with Programming 2018', 'От 3 недель до полутора месяцев', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (23444, 17, 'Обучение, управление (кластер не очень)', false, '<p><span style="font-family: inherit; font-size: 1em; background-color: transparent;">Вы хотите иметь ключевой навык успеха в современном мире?</span><br></p><p>Мы серьезно, какой навык может быть более важным, чем умение общаться, выстраивать взаимоотношения с другими и знать заранее чего ждать от окружающих Вас людей? Коммуникации и взаимоотношения наше все в этом мире! Так давайте улучшать этот навык!<br><br>Цель Международной Академии исследования лжи — обучение специалистов разных профессий дополнительным знаниям, умениям и навыкам в области социальной психологии, профайлинга, безынструментальной детекции лжи, актуальным навыкам, которые позволяют улучшить жизнь, навыки коммуникаций и выстраиванию отношений между людьми.<br><br>За время существования нашей академии мы обучили более:<br>1500 учеников, а возможно и больше!<br>К нам приходят люди из совершенно разных областей деятельности: предприниматели, ТОП-менеджеры, юристы, менеджеры по продажам, адвокаты, директора.<br>На данный момент в академии обучает более 20 преподавателей, часть из которых является основоположниками детекции лжи в России!<br><br>Мы используем только научные подходы в анализе поведения человека, многие инструменты про которые Вы узнаете в данном курсе были опробованы сотнями наших учеников на практике!<br>Филиалы академии сейчас находятся в 11 городах.<br><br>Все наши преподаватели практики! Мы переодически оказываем услуги сторонним организациям в области подбора персонала, проведения проверок сотрудников, а также в решении нестандартных задач!<br><br>Мы искренне верим, что чем больше людей будут владеть инструментами профайлинга, тем более открытым и честным будет мир.<br></p>', '{55460580}', 'self_paced', 7553, 21, 5834, 'ru', 'NA', false, 4.4, 'Профайлинг. Как читать людей?', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/23444/cover_PFe1O73/72f1c60eff3ac27682cb2048c5dfdc2e.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (22402, 11, 'Школьные курсы и егэ по математике', true, '<p>В курсе будут разобраны все прототипы задачи №17 (экономическая задача) из профильного ЕГЭ по математике.</p><p>Курс разбит на модули. На каждой лекции слушателям предлагаются видеоразборы, конспекты и задачи, которые позволят понять, насколько хорошо усвоен материал.</p><p><b>Типы задач, разбираемых в курсе:</b></p><ol><li>Задачи проценты;</li><li>Задачи на вклады;</li><li>Задачи на кредиты;</li><li>Оптимизационные задачи.</li></ol>Возникшие вопросы по заданиям курса вы всегда можете обсудить с однокурсниками и преподавателями в комментариях к задачам. 

<br>', '{37228178,48959503}', 'self_paced', 8241, 190, 97524, 'ru', 'Курс рассчитан на всех, кто хочет научиться решать экономическую задачу из профильного ЕГЭ по математике.', false, 5, 'ЕГЭ по математике: экономическая задача', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/22402/cover_LRNpJMM/717f384177525f043cabc6b97678f1ea.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (10425, 13, 'Мусор2', true, '<p>Пройдя этот курс, вы сможете ответить на несколько популярных вопросов:</p><ol><li>Какими задачами занимаюсь я?</li><li>Чем занимаются моя команда/подчинённые/коллеги?</li><li>Быстро ли команда справляется с потоком задач?</li><li>Какой вклад для бизнеса вносит моя команда и лично я?</li></ol><p>Во всех этих вопросах вам поможет JIRA - ﻿легендарный инструмент для ведения задач команды на электронных досках.<br></p>', '{41305294,34833837,78614866}', 'self_paced', 6420, 20, 8827, 'ru', 'Участники Agile-команд, руководители, желающие структурировать поток своих задач в JIRA', false, 4.78571428571429, 'JIRA: ведение задач на электронных досках', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/10425/cover_hy7uWCO/b7090e7596a3a75fb3b0ddd6e638d26d.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (21115, 4, 'Школьные предметы', true, '<p>Онлайн-курс обществознания рассчитан на абитуриентов вузов, учащихся средних школ и образовательных организаций среднего профессионального образования. Он построен с учетом Федерального государственного образовательного стандарта среднего общего образования (2012) и кодификатора ЕГЭ по обществознанию (на 2019 год). Курс призван помочь:</p>

<p>- учащимся успешно освоить учебную программу и подготовиться к внутренним контрольным испытаниям по обществознанию в своих учебных заведениях;</p>

<p>- абитуриентам подготовиться к ЕГЭ или внутренним вузовским вступительным испытаниям по обществознанию.</p>

<p> </p>

<p>Каждый урок курса включает в себя лекционный материал (видеоролик), промежуточный контроль (тестовые задания) и дополнительные материалы (списки литературы, ключевые слова).</p>

<p>Курс освещает 45 тем, объединенных в пять разделов:</p>

<ol>
	<li>Философия (Человек и общество)</li>
	<li>Социология</li>
	<li>Политология</li>
	<li>Экономика</li>
	<li>Правоведение</li>
</ol>

<p>1. Раздел "Философия" посвящен вопросам происхождения и специфики человека, вопросам познания,. мышления, свободы и ответственности личности, сущности общественной жизни, социальных изменений и культуры.</p>

<p>2.  Модуль "Социология" освещает основные темы социальной жизни: институты и группы, социальная мобильность, стратификация и контроль.</p>

<p>3.  В третьем модуле обсуждаются проблемы власти, государства и гражданского общества, избирательной системы и специфика государственной власти в России.</p>

<p>4. К темам экономического раздела относятся: рынок, экономические системы, налогообложение, финансовые институты, инфляция, рынок труда.</p>

<p>5. Заключительный модуль освещает темы российского и мирового права, законотворчества,  гражданское, избирательное и трудовое право.</p>

<p> </p>
', '{38793712}', 'self_paced', 18503, 218, 34709, 'ru', 'Учащиеся школ, абитуриенты', false, 4.69565217391304, 'Обществознание', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/21115/cover_BFXgXVl/a1df2623d1d8f99bc4a1cdb091164f58.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (20886, 1, 'Мусор1', false, '<p><strong>Курс обучения по гражданской обороне и защите от чрезвычайных ситуаций</strong></p><p><strong>


















</strong></p><p><strong><i></i></strong><i></i></p>


















<p><i></i></p>


















<p><i>В соответствии с постановлением Правительства РФ от 19.04.2017 г. № 470
начиная со 2 мая 2017 года все работодатели должны проводить обязательный
инструктаж сотрудников по гражданской обороне. Целью проведения обучения
является ознакомление с системой ГО и РСЧС (Единая государственная система
предупреждения и ликвидации чрезвычайных ситуаций), разъяснение порядка
действий в случае возникновения военной угрозы или чрезвычайной ситуации.</i></p>

<p><i> </i></p>

<p><i>Данное обучение займет у вас около 30 минут. Просим пройти курс до
15.09.2018 включительно. Обучение включает в себя вводный ознакомительный
