INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (19258, 10, 'Правоведение, психология, история', false, '<p>Курс предлагает изучение истории России через призму документальных свидетельств. В основу курса положено множество разных по виду и времени появления письменных источников.<br></p>', '{49377182}', 'self_paced', 1477, 364, 82806, 'ru', 'Учащиеся 9-11 классов', false, 5, 'История России в документах', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/19258/cover/e7313765edfaefeccd9366854430fca0.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (19059, 11, 'Школьные курсы и егэ по математике', true, '<p>Курс охватывает все темы, необходимые для успешного решения любого задания из базового ЕГЭ по математике.</p><p>Курс разбит на 20 модулей. На каждой лекции слушателям предлагаются видеолекции и конспекты, а также задачи, которые позволят понять, насколько хорошо усвоен материал.</p><p><b>Модули курса:</b></p><ol><li>Вычисления: действия с дробями; <br></li><li>Вычисления: действия со степенями;<br></li><li>Простейшие текстовые задачи: проценты;<br></li><li>Преобразования выражений;<br></li><li>Вычисления и преобразования;<br></li><li>Простейшие текстовые задачи: округление;<br></li><li>Простейшие уравнения;<br></li><li>Прикладная геометрия;<br></li><li>Размеры и единицы измерения;<br></li><li>Теория вероятности;<br></li><li>Чтение графиков и диаграмм;<br></li><li>Выбор оптимального варианта;<br></li><li>Стереометрия, часть 1;<br></li><li>Анализ графиков и диаграмм;<br></li><li>Планиметрия;<br></li><li>Стереометрия, часть 2;<br></li><li>Неравенства;<br></li><li>Анализ утверждений;<br></li><li>Числа и их свойства;<br></li><li>Задачи на смекалку.</li></ol>Возникшие вопросы по заданиям курса вы всегда можете обсудить с однокурсниками и преподавателями в комментариях к задачам. 

<br>', '{37228178,48959503}', 'self_paced', 4422, 739, 68574, 'ru', 'Курс рассчитан на всех, кто хочет решить базовый ЕГЭ по математике на максимальный балл.', false, 4.45454545454545, 'ЕГЭ по базовой математике', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/19059/cover/a6e1cd7e179d800ccbf71611e53baec0.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (18132, 13, 'Мусор2', false, '<div><h2 style="text-align: center;">Курс "Просто о МодульКассе"</h2></div><div><p>Мы собрали весь наш опыт для подготовки краткого и сжатого курса о МодульКассе  для партнеров программы МодульКлуб. Только концентрированные знания - "без воды"!</p><p>Цель курса - дать базовые знания  о продукте и рассказать, как потребности малого и среднего бизнеса учтены в МодульКассе. Для партнеров это добавит понимания конкурентных преимуществ кассы и поможет в разработке коммуникаций с потенциальными клиентами.</p><p>Тому, кто пройдет курс, мы начислим МодульБонусы. Их вы потом сможете обменять на ценные подарки (путешествие, IPhone X, портативную колонку, внешний жесткий диск и многое другое).</p><p>Курс расскажет вам о:</p><ul><li>54-ФЗ "без воды" (всё, что нужно знать тем, кто предлагает онлайн-кассы)</li><li>Онлайн-кассе в целом (из чего состоит, что нужно для её правильного использования)</li><li>МодульКассе (технические характеристики,  варианты приобретения, стоимость и тарифы)</li><li>Конкурентных преимуществах и дополнительных "фишках" МодульКассы (то, чем вы можете аргументировать покупку и привлекать потенциальных клиентов)</li></ul><p>Если после прохождения курса у вас ещё останутся вопросы - задавайте их в комментариях модуля "Завершение курса". За ваши вопросы мы также дадим МодульБонусы! <br></p></div>', '{52539150}', 'ended', 304, 13, 1478, 'ru', 'NA', false, 4.66666666666667, 'Просто о МодульКассе', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2645, 15, 'Курсы на английском языке по разным тематикам', true, '<p>Everybody makes pronunciation mistakes at any level, Elementary, Intermediate or Advanced, anyway, you''ve got some fossilized errors in this area. They just cling to you and then you are just stuck with them for a really long time. This is more of a practice medium than a real course. Its structure is easy to grasp for any level as well. Enjoy and hope, it''ll be useful!

