INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (17830, 17, 'Обучение, управление (кластер не очень)', false, '










<p><b>О курсе</b></p><p>Курс является неотъемлемой частью образовательного проекта "БудуГуру — молодежный гид по карьере в IT",  созданного, чтобы помочь молодым людям найти интересную и подходящую им специальность в области информационных технологий и развиваться в ней.</p><p>В рамках курса мы ставим перед собой цель превратить вас из специалистов прошлого в специалистов будущего. Мы предлагаем освоить навыки гибкого мышления, и на долгие годы обезопасить себя от замещения машиной. Вы ведь не хотите оказаться на обочине прогресса?</p>
<p>Вы изучите и сможете применять <b>5 актуальных подходов</b>:</p>
<ul>
<li><b>Креативное мышление</b><br>
Разорвет шаблоны и превратит генерацию идей из хаоса в упорядоченный процесс<br><br>
</li>
<li><b>Системное мышление</b><br>
Позволит увидеть вокруг себя системы и связи, понять их и начать действовать эффективно с их помощью.<br><br>
</li>
<li><b>Критическое мышление</b><br>
Подскажет, как каждый день делать осознанный выбор в свою пользу.<br><br>
</li>
<li><b>Предпринимательское мышление<br>
</b>Пробудит вашу активную позицию и готовность действовать, а также проверять свои результаты, чтобы двигаться дальше.<br><br></li><li><b>Эффективное обучение</b><br>
Выступит навигатором в современном потоке информации и поможет разложить по полочкам полученные знания.</li></ul>
<p>Каждый из подходов сделает вас профессионалом, а владение несколькими сделает просто незаменимым в современной индустрии поли-профессионалом. Вы будете управлять не только своей карьерой, но и жизнью в целом. Мы не читаем нудные лекции, а даем практические инструменты для решения широкого спектра личных и профессиональных проблем. После прохождения курса, вы будете мыслить гибко, а также обретете новый взгляд на решение сложных задач и достижение вашей цели.</p><p><b>Для кого этот курс?</b></p><ul>
<li><b>Для старшеклассников</b>, которые еще не определились с выбором будущей перспективной специальности</li>
<li><b>Для студентов</b>, которые хотят получить больше знаний и опыта, чтобы получить хорошую работу </li>
<li><b>Для молодых специалистов</b>, которые хотят сменить направление своей деятельности</li></ul><div>Больше информации об образовательном проекте "БудуГуру —﻿ молодежный гид по карьере в IT" вы можете найти на официальном сайте: <a href="http://buduguru.org/" rel="nofollow noopener noreferrer" title="Link: http://buduguru.org/">http://buduguru.org</a><br></div><div><br></div><div>Проект реализован с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.<br></div>', '{57310195}', 'self_paced', 970, 5, 10093, 'ru', 'NA', false, 4.6, 'Гибкое мышление', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/17830/cover_bPo44Ay/da8b4adb7182fcaf77118a53a4a3de8b.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (16937, 1, 'Мусор1', true, '<p>Курс от компании HPBS (<a href="http://www.hpb-s.com" rel="nofollow noopener noreferrer" title="Link: http://www.hpb-s.com)">www.hpb-s.com)</a>.</p><p>На тренинге будут освещены следующие темы:<br><br>1.  обзор зеленого строительства и стандарт LEED<br>2.  финансово-экономические аспекты, добавленная ценность, финансирование и капитализация активов<br>3.  критерии стандарта LEED BD+C версии v4 по областям знаний:<br>4.  местоположение и транспорт<br>5.  благоустройство участка<br>6.  водопотребление<br>7.  энергоэффективность и атмосфера<br>8.  материалы строительства<br>9.  качество внутренней среды<br>10.  инновации в проектировании и региональные приоритеты<br>11.  практические вопросы/тесты<br><br>Курс дает общие понимание тенденций в передовых международных нормах и практиках проектирования и строительства.<br></p>', '{49285048}', 'self_paced', 851, 30, 28154, 'ru', 'Данный курс ориентирован на архитекторов, инженеров строителей, производителей материалов, оборудования, инвесторов в недвижимость, застройщиков, девелоперов, а также административных директоров и собственников недвижимости.', false, 5, 'Подготовка к аккредитации LEED GA / AP', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/16937/cover/2cdde14ef7c0c63a775bab50ff07e0ea.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (16657, 7, 'Программирование и анализ данных для продолжающих', false, '<p>Курс должен позволить обучающемуся приобрести знания, умения и навыки программирования на трех императивных языках и одном декларативном языке программирования. Также курс должен обеспечить мотивацию к дальнейшему совершенствованию работы на языках, которые окажутся наиболее близкими для обучающегося. Использование ddl библиотек сможет обеспечить возможность совместного использования двух и более языков программирования. Курс предполагает возможность объединения обучающихся в команды для совместного освоения курса и совместной работы. Предполагается работа в таких командах программистов с недостаточно идеальным зрением.    </p>', '{41333531}', 'self_paced', 371, 71, 4128, 'ru', 'Студенты, желающие получить общие знания, умения и навыки программирования по нескольким дополнительным языкам программирования с точки зрения их структурных соответствий.', false, 5, 'Сравнительное Программирование (Delphi - C++ - Java - Prolog)', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/16657/cover_sp7h6oT/cd4f8e160ea3939ff0c44706292655e4.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (16640, 7, 'Программирование и анализ данных для продолжающих', true, '<p>

Современная жизнь немыслима без web-сервисов, ежедневно приносящих дивиденды в самых разных областях человеческой деятельности. А значит, профессия web-разработчика еще долго будет оставаться высокооплачиваемой и востребованной на рынке IT-специалистов. Существует множество решений и инструментов на разных языках программирования, упрощающих и ускоряющих web-разработку. В рамках данного курса освещается построение web-приложений на языке Python.
Курс читают разработчики, применяющие Python в проектах, которыми ежедневно используют миллионы людей. В данном курсе вы сможете приобрести как базовые знания о функционировании современного интернета в целом, так и практические навыки создания интернет-приложений на языке Python.

<br></p>', '{43769607}', 'self_paced', 238, 139, 153200, 'ru', 'NA', true, 4.75, 'Создание Web-сервисов на Python', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/12258/cover/be7ebae7103ad25642ad0a39a8667ad1.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (16639, 7, 'Программирование и анализ данных для продолжающих', true, '<p>

Курс возводит слушателя от написания простых конкретных классов к профессиональному конструированию приложения в объектно-ориентированной парадигме. Паттерны проектирования позволяют шагнуть за пределы простого использования синтаксических конструкций языка. Вы научитесь писать красиво и элегантно, будете использовать проверенные временем концепции и создавать масштабируемые программы. Использование паттернов проектирования является признаком профессионализма программиста.	
Классические книги по паттернам проектирования описывают их реализацию на C++, C#, Java. У языка Python есть своя специфика из-за которой он отлично подходит для использования паттернов проектирования.

<br></p>', '{43769607}', 'self_paced', 234, 74, 61149, 'ru', 'NA', true, 5, 'ООП и паттерны проектирования в Python', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/12241/cover_hMIlKCx/00c8df0c5ef0e9cde07ec569bf394086.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (16638, 7, 'Программирование и анализ данных для продолжающих', true, '<p>Python – простой, гибкий и невероятно популярный язык, который используется практически во всех областях современной разработки. С его помощью можно создавать веб-приложения, писать игры, заниматься анализом данных, автоматизировать задачи системного администрирования и многое другое. “Погружение в Python” читают разработчики, применяющие Python в проектах, которыми ежедневно пользуются миллионы людей. Курс покрывает все необходимые для ежедневной работы программиста темы, а также рассказывает про многие особенности языка, которые часто опускают при его изучении. </p><p>В ходе курса вы изучите конструкции языка, типы и структуры данных, функции, научитесь применять объектно-ориентированное и функциональное программирование, узнаете про особенности реализации Python, научитесь писать асинхронный и многопоточный код. Помимо теории вас ждут практические задания, которые помогут проверить полученные знания и отточить навыки программирования на Python. После успешного окончания курса вы сможете использовать полученный опыт для разработки проектов различной сложности.

<br></p>', '{43769607}', 'self_paced', 240, 158, 171324, 'ru', 'Студенты, выпускники, профессиональные аналитики.', true, 4.625, 'Погружение в Python', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/11553/cover/0c00cb4ff148f9ff3a814b36c836587d.jpg');
