INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (14905, 1, 'Мусор1', true, '<p>В течение многих лет специалисты «Лаборатории Касперского» обнаруживают и исследуют киберугрозы, направленные на различные информационные системы — коммерческих и государственных организаций, банков, телеком-операторов, промышленных предприятий и частных лиц.</p>

