INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2054, 13, 'Мусор2', true, '<p>Курс посвящён основам филогенетики и современным методам, применяемые для построения филогении по данным последовательностей нуклеиновых кислот или белков. Кроме того, мы рассмотрим некоторые другие темы, необходимые для понимания закономерностей эволюции.<br></p>', '{651763}', 'self_paced', 4964, 106, 34658, 'ru', 'Студенты-биологи  и биоинформатики', false, 4.80952380952381, 'Молекулярная филогенетика', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/2054/cover/8db6d7778dd39b948e9dcc6b42ff74e0.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (6641, 4, 'Школьные предметы', false, '<p>Информатика, 9 класс. </p><p>В курсе предлагаются задачи по информатике 9 класса базового уровня. Примеры на программирование разбираются на языках JavaScript и Python. </p><p>Курс является авторским, что означает, что в рамках курса авторы являются бесспорной истиной в последней инстанции, но не лишены ошибок, включая непростительные.</p><p>Обратите внимание, этот курс</p><p>﻿  -- не является курсом обучения JavaScript или Python, оба языка здесь представлены в упрощенном виде<br></p><p>  -- не является учебным курсом по информатики 9 класса, а лишь учебным материалом к нему</p><p>  -- не является курсом по алгоритмам или подготовке к олимпиадам</p><p>  -- не является курсом по подготовке к сдаче ОГЕ</p>', '{2650967}', 'active', 346, 49, 133501, 'ru', 'NA', false, 4, 'Информатика, 9¾ класс', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/6641/cover_KCFBHh6/f4a5b1bd20c2ac13518b317c53d4baca.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5842, 13, 'Мусор2', false, '<p>Настоящий курс лекций затрагивает многие из наиболее актуальных проблем в сфере содействия международному развитию (СМР), которые рассматриваются с позиций междисциплинарного анализа. Такой междисциплинарный подход отражает не только многообразие профессиональных интересов участников проекта, но и многоаспектность самого процесса развития и необходимость изучения всего спектра направлений экономической и социальной трансформации.<br></p><p>Курс лекций имеет модульную структуру, что позволяет формировать на его основе различные по продолжительности и тематической специфике учебные программы.</p><p><span style="background-color: transparent; font-family: inherit; font-size: 1em;"><a href="https://vk.com/developmentonlinecourse" rel="nofollow noopener noreferrer" title="Link: https://vk.com/developmentonlinecourse">https://vk.com/developmentonlinecourse</a></span></p><p><span style="background-color: transparent; font-family: inherit; font-size: 1em;"><a href="https://vk.com/developmentonlinecourse" rel="nofollow noopener noreferrer" title="Link: https://vk.com/developmentonlinecourse"></a></span></p><p style="text-align: center;"><a href="https://vk.com/developmentonlinecourse" rel="nofollow noopener noreferrer" title="Link: https://vk.com/developmentonlinecourse"><img class="image-fullsize" src="https://ucarecdn.com/ae061af5-dd06-4012-a7f9-ed93ce1a317d/" title="Image: https://ucarecdn.com/ae061af5-dd06-4012-a7f9-ed93ce1a317d/"></a></p><a href="https://www.facebook.com/developmentonlinecourse/" rel="nofollow noopener noreferrer" style="font-family: inherit; font-size: 1em;" title="Link: https://www.facebook.com/developmentonlinecourse/﻿"></a><br><p><a href="https://www.facebook.com/developmentonlinecourse/" rel="nofollow noopener noreferrer" title="Link: https://www.facebook.com/developmentonlinecourse/">﻿https://www.facebook.com/developmentonlinecourse/</a>

﻿</p><p><span style="background-color: transparent; font-family: inherit; font-size: 1em;"><a href="https://www.facebook.com/developmentonlinecourse/" rel="nofollow noopener noreferrer" title="Link: https://www.facebook.com/developmentonlinecourse/﻿"></a></span></p><p style="text-align: center;"><a href="https://www.facebook.com/developmentonlinecourse/" rel="nofollow noopener noreferrer" title="Link: https://www.facebook.com/developmentonlinecourse/﻿"><img class="image-fullsize" src="https://ucarecdn.com/c018af8f-0e9c-40d0-929b-539dd457dab5/" title="Image: https://ucarecdn.com/c018af8f-0e9c-40d0-929b-539dd457dab5/"></a></p><p><font color="#0000ff"><u>﻿<br></u></font></p>', '{}', 'self_paced', 303, 78, 5785, 'ru', 'Отдельные лекции могут служить дополнением к уже существующим учебным курсам в вузах и институтах переподготовки, а также использоваться студентами и научным сообществом в научно-практических и аналитических целях.', false, 4.66666666666667, 'Содействие международному развитию: теория и практика', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/5842/cover_JmXPujY/f027f4ffe73686273023d3ec6e97df0e.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (6405, 9, 'Экономика, менеджмент', true, '<p>Сегодня в более чем половине случаев использование любых цифровых продуктов происходит с мобильных устройств. Рекламная экосистема Facebook даёт доступ к большой и качественной аудитории пользователей мобильных устройств в мире, что делает крайне важным инструментом в руках современного маркетолога.</p>
<p>Курс построен на основе практического опыта привлечения пользователей в мобильные приложения клиентов агентства Ad4top, среди которых Alibaba Group, Topface, Yandex и другие компании. Содержание курса было разработано в рамках программы стажировки, направленной на подготовку специалистов без опыта работы.</p>
<p>Знания, полученные на курсе, могут быть применены как для продвижения собственного приложения, так и для начала карьеры в одной из самых востребованных профессий.</p>
