INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (6303, 0, 'Языки', false, '<p>В данном курсе моделируются реальные рабочие ситуации: от письма англоязычному партнеру и обсуждения нового проекта до выступления на зарубежной конференции. Вы изучите профессиональную лексику, деловую терминологию и сможете без труда понимать зарубежных коллег, четко выражать свои мысли и отстаивать свою точку зрения.</p>

