INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (6097, 13, 'Мусор2', true, '<div><div><div><div><div><div><div><div><div><div><div>

В курсе рассматриваются основные понятия и определения, классификации, используемые в томографии, области применения томографии и ее практическая значимость, обозначаются перспективы развития, а также показываются передовые томографические комплексы, разработанные в Томском политехническом университете.<br><br></div><div>В процессе прохождения курса вы получите ответы на следующие вопросы:<br>– когда и кем был изобретен первый томограф (кто был основоположником томографии);<br>– как и по каким признакам классифицируют томографы;<br>– насколько безопасна томография;<br>– где находятся самые передовые разработки в области томографии;<br>– каково будущее томографии;<br>– как стать востребованным специалистов в области томографии;<br>– сколько ученых, занимающихся исследованиями и разработками в томографии стали лауреатами Нобелевской премии и многое другое.<br><br></div><div>После прохождения курса у вас появится понимание, что томография — это просто и интересно, а карьера в области томографии позволит реализовать себя и удовлетворить свои амбиции, даже очень притязательной личности.

</div></div></div></div></div></div></div></div></div></div></div>', '{47702322}', 'active', 1863, 63, 10480, 'ru', 'Курс предназначен для студентов профильных специальностей, специалистов в области неразрушающего контроля, интересующихся томографией. А также для тех, кто хочет познакомиться с возможностями, которые открывает для нас томография', false, 4.61904761904762, 'Томография: увидеть невидимое', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/6097/cover_QQxXYvH/2984e4778018928fb5cd9572ec849bbc.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (6095, 17, 'Обучение, управление (кластер не очень)', true, '<p>Уникальность курса состоит в гармоничном сочетании теории и практики. <span style="font-size: 1em; background-color: transparent;">В нашем курсе найдут полезную и важную информацию все. Для больших предприятий – информация о применении логистического подхода при настройке своих бизнес-процессов. Для малого бизнеса – шаблон для самостоятельного ведения складского учета. Для простого человека – решение бытовых логистических проблем – транспорт, закупка продуктов и размещение продуктов в холодильнике.</span></p><p>По завершению курса слушатели смогут использовать самостоятельно созданный в программе MS Excel шаблон для принятия логистических решений для оптимизации реальных бизнес-процессов своей компании.<br></p>', '{47702322}', 'active', 10315, 120, 22313, 'ru', 'Курс предназначен для студентов и магистрантов экономических направлений, предпринимателей (представителей небольшого производства), желающих создать свой бизнес', false, 4.78260869565217, 'Сам себе логист. Развиваем предпринимательское мышление', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/6095/cover_DEuLQsZ/3752b2c7ec40c386c2465ed853509918.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5582, 0, 'Языки', true, '<p>Курс посвящён применению английского языку для биологов: чтению и написанию текстов, восприятию на слух и общению — и составлен в новом формате: тексты и аудиозаписи.</p>

<p>В первую очередь курс рассчитан на студентов-биологов, начинающих свою карьеру в мировой науке, но может быть не менее полезен более зрелым учёным, чувствующим себя недостаточно комфортно при общении по-английски.<br>
<br>
Курс не обязательно проходить целиком и по порядку: можете заглядывать в те главы, которые сейчас вас интересуют.</p>
', '{3286,26841420}', 'self_paced', 4631, 251, 38004, 'ru', 'Биологи (студенты и не только), желающие подтянуть английский.', false, 4.85714285714286, 'Английский для биологов', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/5582/cover_nUl5qTW/08ccb10b3eb082d4f6c8e7184c0d43a3.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5917, 9, 'Экономика, менеджмент', false, '<p>Учебная программа «SMM- менеджмент» 
предназначена  для   слушателей категории людей  с ограниченными возможностями здоровья  с целью 
вовлечения  их в сферу
интеллектуальной трудовой деятельности и направлена на реализацию  их потребностей комплексной реабилитация и
последующей самореализации.</p><p>

Обучение проводится в рамках реализации проекта «ИнваТех-ИМ» (содействие вовлечению молодых людей с ограниченными возможностями здоровья в сферу интеллектуальной трудовой деятельности). <br>Проект получил грант Президента Российской Федерации на развитие гражданского общества, выданный Фондом Президентских грантов .<br>Проект реализуется РЕГИОНАЛЬНЫМ ОТДЕЛЕНИЕМ ОБЩЕРОССИЙСКОЙ ОБЩЕСТВЕННОЙ ОРГАНИЗАЦИИ ИНВАЛИДОВ-БОЛЬНЫХ РАССЕЯННЫМ СКЛЕРОЗОМ РЕСПУБЛИКИ ХАКАСИЯ "ОПЛОТ"

<br></p>

<p>Целью программы является освоение профессиональных компетенций в области SMM-
менеджмента  для подготовки к практической 
профессиональной деятельности в сфере 
продвижения  компании или продукта
в социальных сетях как необходимого условия  успешного трудоустройства .</p>

<p>Задачи программы</p>

<p>-  формирование у слушателя системы знаний об основных элементах SMM- менеджмента, рекламной деятельности в социальных
сетях;</p>

<p> - освоение
технологии  и механизмов создания 
Internet - сообществ и наполнении их 
контентом;</p>

<p>- формирование
умений  разработки стратегии
продвижения  и  составления бюджета;</p>

<p>- формирование
умений работы с таргетированной и 
контекстной рекламой;</p>

<p>- формирование  коммуникативных навыков для привлечения
подписчиков и «лидеров мнений»,
активизации  процессов  их общения.</p>

<p>В результате обучения
слушатели получат следующие профессиональные 
компетенции:</p>

<p>1. Стратегического
планирования  в сфере разработки и
реализации  программы продвижения и
маркетинга компании, продуктов, бренда.</p>

<p>2. Управления рекламным
процессом и процессом формирования обратной связи с целевой аудиторией на Internet –площадках.</p>

<p>3.  Организации коммуникаций на Internet
–площадках с потенциальными потребителями и подрядчиками</p>





<br>', '{30592977,42542447}', 'self_paced', 15314, 47, 35548, 'ru', 'Люди с ограниченными возможностями здоровья', false, 4.15, 'SMM - менеджмент', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/5917/cover_i2h2U1X/6925e8f949758bb6ccbc9ec706e2d201.gif');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5779, 14, 'Мусор3', true, '<p><b>Данный курс это:</b><br><br>1.Путеводитель по миру онлайн-курсов, одному из, если не самому обсуждаемому явлению в образовательном мире сегодня. Как у любого нововведения у них есть достоинства и недостатки. Национальный исследовательский Томский государственный университет разрабатывает МООК с 2014 года и предлагает их на таких платформах, как Национальная платформа Открытое образование, Coursera, Лекториум, Stepik, Образование на русском. Курс «Онлайн-технологии в обучении» позволит Вам сформировать представление о МООК, основанное на своём личном опыте.<br><br>2. Библиотека полезных онлайн-ресурсов, которые помогут вам эффективно работать с документами, красочно презентовать свой материал, работать с аудиторией в режиме реального времени, планировать встречи и реализовать любые свои задумки.</p><p><b>Программа курса:</b><br></p><p>1. Знакомимся с онлайн-технологиями<br>2. Эффективно обучаемся на онлайн-курсах<br>3. Включаем онлайн-курсы в свою учебу</p><p><b>Результат обучения:</b> </p><ul><li>способность к поиску информации, самообучению и самоорганизации, планированию работ и принятию решений, способность к сотрудничеству и работе в команде;</li><li>готовность самостоятельно организовать свое обучение;</li><li>готовность использовать новые информационные технологии и средства коммуникации;</li><li>способность осуществлять выбор своей образовательной траектории, ее формирование с включением онлайн-курсов в индивидуальный учебный план.</li></ul>

<p><b>Команда курса:</b></p>

<p>Ульяна Захарова — автор-преподаватель, методист курса.
Методист и аналитик образовательных онлайн-проектов Томского государственного
университета</p><p>

Андрей Пархоменко — автор-преподаватель, оператор курса. Оператор массовых открытых онлайн-курсов Томского государственного университета</p><p><br></p>', '{33163326}', 'self_paced', 10379, 29, 27550, 'ru', 'Курс ориентирован на студентов, учителей и преподавателей, а также всех тех, кто находится в поиске возможностей для непрерывного обучения без отрыва от учёбы и работы, кто интересуется МООК и хотел бы на них обучаться.', false, 4.90625, 'Онлайн-технологии в обучении', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/5779/cover/565a587834bfffebe5e64d476fa48b8d.jpeg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5703, 7, 'Программирование и анализ данных для продолжающих', true, '<p>Данный курс предназначен для ознакомления с основными принципами разработки приложений для платформы Android. Мы надеемся, что курс будет интересен тем, кто хочет создавать мобильные приложения, но не знает с какой стороны начать. В нем рассказаны основные моменты, без которых не обходится ни одно приложение. Курс ознакомительный и затрагивает только главные темы, не углубляясь в детали и варианты использования компонентов. <br></p><p>Примеры кода, использованного в лекциях можно посмотреть здесь <a href="https://github.com/VBorodinTT/AndroidLectures" rel="nofollow noopener noreferrer">https://github.com/VBorodinTT/AndroidLectures</a>﻿</p>', '{41703762,49345364}', 'self_paced', 13460, 25, 13512, 'ru', 'Студенты средних и старших курсов, специалисты IT-профиля.', false, 4.19047619047619, 'Разработка мобильных приложений для Android', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/5703/cover/97d668268c461abcce9708971d58e556.PNG');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5608, 12, 'Мне повезет', true, '<p>Данный курс предназначен для тех слушателей, которые хотят прослушать полный курс графов в объеме, примерно соответствующем аналогичному курсу, читаемому студентам-бакалаврам 2-го курса, обучающихся по программе "Прикладная математика и информатика" на факультете "Санкт-Петербургская школа физико-математических и компьютерных наук". Курс охватывает все основные разделы теории графов, необходимые как программистам, так и математикам, работающим в области дискретной математики.</p>', '{187813}', 'active', 3008, 85, 133917, 'ru', 'NA', false, 4.25, 'Теория графов', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/5608/cover/7b0d15057662f310a1ed03bb9066e82a.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5599, 1, 'Мусор1', false, 'Ass', '{37074562}', 'self_paced', 2459, 35, 64775, 'ru', 'Учащиеся 7-10 классов средних школ', false, 2.33333333333333, 'Основы языка Pascal', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/5599/cover/a9299589d9b30e76b068e006d2f1bc9c.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5448, 5, 'Введение в основы программирования и анализ данных', false, '<p><i></i><i></i><i></i><b><i></i></b><i></i><i></i><b>Курс находится в стадии разработки -- шаги и уроки могут добавляться, меняться и удаляться из курса.</b><i></i><i></i><br></p><p>В данном курсе учащимся предлагается изучить язык Kotlin. Kotlin является современным языком программирования, созданным компанией JetBrains и поддерживаемый сообществом. Язык может компилироваться в JVM-байткод и в JavaScript, сейчас разрабатывается платформа Kotlin native.</p><p>Изучение проходит на платформе JVM.<br></p><p>﻿Не стану отрицать, в курсе могут быть ошибки. Если вы заметили ошибку, недоработку или опечатку, либо сомневаетесь в правильности изложенного материала - пожалуйста, не стесняйтесь написать в комментариях - это очень нам поможет.<br></p>', '{35787766}', 'self_paced', 7399, 32, 38762, 'ru', 'NA', false, 4.5, 'Введение в Kotlin JVM', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/5448/cover_1d8eoFE/8e827c35080909b7fbdd0b68634ff15a.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5444, 13, 'Мусор2', false, '

В этом курсе по проектированию в рамках среды Autocad будет подробно разобран каждый из этапов, которые выполняются при разработке проекта, начиная от поступления запроса клиента, до выдачи ему готовой проектной документации.<div>В рамках каждого этапа будут рассмотрены основные понятия, стандарты и инструменты автоматизации, которые необходимо применять для повышения эффективности. </div><div>Для закрепления пройденного материала будет предложено прохождение заданий, тестирование, самостоятельная работа.</div><div>Видео по работе команд из пакеты clk-tools можно найти на канале YouTube ﻿(на текущий момент в разработке):</div><div><a href="https://www.youtube.com/channel/UC1VUPuK-ks98861vMNcnlUw" rel="nofollow noopener noreferrer" title="Link: https://www.youtube.com/channel/UC1VUPuK-ks98861vMNcnlUw">https://www.youtube.com/channel/UC1VUPuK-ks98861vMNcnlUw</a>﻿<br></div>', '{41085523}', 'ended', 401, 5, 1434, 'ru', 'Сотрудники проектных компаний, студенты.', false, 4, 'Эффективное проектирование в ACAD', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (68562, 13, 'Мусор2', false, '<p>В данном курсе учащиеся познакомятся со штангенциркулем с точностью измерения 0,1 мм. Узнают историю создания инструмента. Научатся правильно пользоваться и измерять различные отверстия и выступы. Узнают что такое нониус и кто его изобрел. Смогут правильно считывать показания со шкал штанген-инструментов.  </p>
', '{194732904}', 'self_paced', 73, 15, 1242, 'ru', 'Учащиеся 6-го класса, а так-же все желающие научиться пользоваться штангенциркулем.', false, 5, 'Технология. Устройство и измерения штангенциркулем.', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/68562/cover_56HTbMY/f25a56958a2bbd5ffcede6a0b282ad25.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5423, 17, 'Обучение, управление (кластер не очень)', true, '<p>Курс основан на материалах магистерских курсов, которые автор в течение последних 20 лет читал в Центрально-Европейском Университете в Будапеште, Манчестерском университете, а также в Европейском университете в Санкт-Петербурге. Курс опирается на следующие книги, изданные автором как автором или редактором: «Город империи в годы войны и революции, 1914-1921» СПб, Нестор-История, 2017; Нация, или Могущество мифа. СПб, ЕУ Пресс, 2016; Nationalizing Empire. (with Stefan Berger) CEU Press, Budapest, 2015; «Понятия о России». К исторической семантике имперского периода. М., НЛО, 2012. Т. 1, 2; Украинский вопрос в Российской империи. Kiev, Laurus, 2013; Империя Романовых и национализм. М., НЛО, 2010 (2 изд.); Imperium inter pares. Роль трансферов в истории Российской империи. М., НЛО, 2010; Западные окраины Российской империи. НЛО, 2008 (2 изд.); Национализм и империя. M., ОГИ, 2005; Imperial Rule. (with Alfred J. Rieber) N.Y.-Budapest, CEU Press, 2004; Национализм и формирование наций. Теории-модели-концепции. М., Institute for Slavic Studies, 1994.</p>

