INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5420, 0, 'Языки', false, '<p><strong>Приветствую вас в моем курсе самостоятельного изучения английского языка по видеоклипам любимых исполнителей для учащихся с уровнем английского языка от Intermediate и выше.</strong></p>

