INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5352, 13, 'Мусор2', false, '<p>Курс охватывает все темы, необходимые для успешного решения любого типа неравенств из школьной программы: от простейших линейных до достаточно сложных показательных, логарифмических и тригонометрических.</p><p>Курс разбит на 8 модулей. На каждой лекции слушателям предлагаются тесты и задачи, которые позволят понять, насколько хорошо усвоен материал.</p><p><b>Модули курса:</b></p><ol><li>Алгебраические и рациональные неравенства;</li><li>Иррациональные неравенства;</li><li>Неравенства, содержащие модуль;</li><li>Показательные неравенства;</li><li>Логарифмические 

неравенства;</li><li>Тригонометрические 

неравенства;</li><li>

Дополнительные методы решения неравенств;<br></li><li>

Метод замены множителя (МЗМ).<br></li></ol>Возникшие вопросы по заданиям курса вы всегда можете обсудить с однокурсниками и преподавателями в комментариях к задачам.

<br>', '{37228178,48959503}', 'self_paced', 1160, 343, 104920, 'ru', 'Курс рассчитан на всех, кто хочет научиться решать неравенства, в особенности учеников 9-11 классов, сдающих ОГЭ и ЕГЭ.', false, 5, 'Неравенства', 'От полутора месяцев и больше', 'https://stepik.org/media/cache/images/courses/5352/cover/e816234efbc8ca77ff668e4cf312f71c.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (5304, 9, 'Экономика, менеджмент', true, 'Реформа здравоохранения предполагает
