INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2041, 13, 'Мусор2', false, 'В курсе раскрывается концепция заданий, методика судейства в следующих состязаниях:<br>- Основная категория<br>- Студенческая категория<br>- Футбол роботов<br>- Сервисные и Промышленные Интеллектуальные Робототехнические Системы<br><br>Курс предназначен для судей региональных этапов и заключительного этапа Всероссийской Робототехнической Олимпиады 2017', '{23224612}', 'self_paced', 636, 74, 20551, 'ru', 'Тренеры команд и судьи состязаний по олимпиадной робототехнике', false, 4.25, 'Методика судейства и концепция состязаний ВРО 2017', 'От полутора недель до 3 недель', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (4484, 17, 'Обучение, управление (кластер не очень)', false, '<p>
















