INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2159, 7, 'Программирование и анализ данных для продолжающих', true, '<p>Git version control system is a de-facto standard now and each software developer should understand its basics. The course aims to cover most used commands and describe common use cases of it.  </p><p>This course can be also passed using non-adaptive (traditional manner) using this <a href="https://stepik.org/course/Applied-Computer-Science-Git-2159/syllabus" rel="nofollow noopener noreferrer">LINK</a>.</p><p>﻿We <b>recommend</b> you use following <a href="https://stepik.org/lesson/Getting-started-with-git-sandbox-43667" rel="nofollow noopener noreferrer" title="Link:  https://stepik.org/lesson/Getting-started-with-git-sandbox-43667 .">lesson</a> as a <b>sandbox for this course.</b></p><p>Following materials are recommended as additional guides for course participants:</p><ol><li>Git manual <a href="https://git-scm.com/docs" rel="nofollow noopener noreferrer" title="Link: https://git-scm.com/docs">https://git-scm.com/docs</a><br></li><li> Pro Git book, written by Scott Chacon and Ben Straub <a href="https://git-scm.com/book/en/v2" rel="nofollow noopener noreferrer">https://git-scm.com/book/en/v2</a><br></li><li>Git cheatsheets <a href="http://ndpsoftware.com/git-cheatsheet.html" rel="nofollow noopener noreferrer">http://ndpsoftware.com/git-cheatsheet.html</a> and <a href="https://services.github.com/on-demand/downloads/github-git-cheat-sheet.pdf" rel="nofollow noopener noreferrer">https://services.github.com/on-demand/downloads/github-git-cheat-sheet.pdf</a></li></ol>', '{17406227}', 'self_paced', 5277, 27, 6221, 'en', 'Beginners and people who want to build solid understanding of git.', false, 4.6, 'Applied Computer Science. Git', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/2159/cover/91abe3f6772eb2ae0b64e30d53b725fd.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2154, 7, 'Программирование и анализ данных для продолжающих', false, '<div><span style="background-color: transparent; font-size: inherit;">By the end of this course you will - in very simple steps- learn how to model a real world process and use R to run simulations and analyse data resulting from running these simulations. Y</span><span style="background-color: transparent; font-size: inherit;">ou will practice everything your self starting from modelling a process using pen and paper, feed this model into software, adjust parameters, run the simulation and analyse resulting data to predict the performance of this process.</span></div><div>In this course, we will use Discrete Event Simulation with an example from Healthcare industry.<br></div><div>We will have no long boring talks, just a glimpse of the concept followed by a "do it yourself" assignments.<br></div><div>Good Luck !</div>', '{29409278}', 'self_paced', 1113, 21, 1883, 'en', 'NA', false, 5, 'Modeling and Simulation using R', 'От 1 до 4 дней', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2152, 2, 'Математический анализ, статистика', true, '<p>В данном курсе мы завершаем введение в наиболее распространенные методы анализа данных. Мы подробнее разберем методы регрессионного анализа, уделив особое внимание методам диагностики регрессионных моделей. Познакомимся с такими продвинутыми методами как смешанные регрессионные модели, узнаем что такое 

bootstrap и как его применять для проверки гипотез. Помимо теоретических заданий слушателей ожидают практические задачи, которые необходимо выполнять, используя язык программирования R. Данный курс будет полезен как для слушателей из академических, так и из прикладных областей.</p>', '{651763}', 'self_paced', 11006, 33, 37494, 'ru', 'Студенты и научные работники всех специальностей, школьники старших классов.', false, 4.19047619047619, 'Основы статистики. Часть 3', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/2152/cover_SwXpUSR/6bb13f3701d44ae6814ce9726fff8645.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1591, 4, 'Школьные предметы', false, '<p>Краткий курс по теме "Тип членистоногие. Класс Насекомые" рассчитан на учащихся 7 класса, изучающих зоологию.</p><p>Курс содержит краткий необходимый теоретический материал, много ярких иллюстраций по теме, несколько видео, помогающих усвоить данную тему. Желаю успеха!<br></p>', '{22985341,15946533}', 'self_paced', 94, 37, 3146, 'ru', 'учащиеся 7 класса общеобразовательных школ', false, 5, 'Класс Насекомые', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/1591/cover/2814dff578559bb58888e56e2d2268f4.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2096, 16, 'НКО (некоммерческие организации)', true, '<p> </p>

<p>Некоммерческие и благотворительные проекты чаще всего нуждаются в финансировании. Где взять эти средства? Привлечь как пожертвования? Заработать самим, создав социальный бизнес?</p>

<p>Курс рассказывает о том, кто дает средства и учит, как их получить. Вы научитесь успешно привлекать ресурсы на некоммерческие проекты – из разных источников. На российских примерах вы увидите, в каких внутренних и внешних источниках можно искать средства. Поймете, как туда обратиться и как стать успешным в поиске средств. Разберетесь в терминологии – сможете уверенно различить спонсорство, краудфандинг, соцпредпринимательство, кобрендинг и другие похожие явления.</p>

<p>Вы поймете:</p>

<p>• из каких внешних и внутренних источников можно поддерживать некоммерческие проекты<br>
• как работать с каждым источником средств<br>
• в какие источники лучше обратиться именно вам и как их сочетать</p>

<p><strong>Программа курса:</strong></p>

<p>Модуль 1. Обзор современного фандрайзинга<br>
Модуль 2. Как привлечь деньги фондов и государственные средства<br>
Модуль 3. Как привлечь деньги компаний<br>
Модуль 4. Как привлечь частные пожертвования офлайн и онлайн<br>
Модуль 5. Внутренние источники средств: социальное предпринимательство, оптимизация расходов, членские взносы и т.д.</p>

<p>В конце вас ждет итоговый тест.</p>

<p>Курс организован <a href="http://www.crno.ru/" rel="nofollow noopener noreferrer" style="font-size: 1em;">Санкт-Петербургским Центром развития некоммерческих организаций</a> (ЦРНО), и проводится в рамках проекта «Повышение квалификации сотрудников НКО онлайн: быстро, качественно и практично». Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>
', '{28878375}', 'self_paced', 5213, 56, 16609, 'ru', 'Курс базовый – хорошо подойдет для начинающих НКО-шников и соцпредпринимателей, а также для всех, кто хочет быстро увидеть комплексную картину по фандрайзингу и предпринимательству.', false, 4.96, 'Поиск средств для НКО', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/2096/cover/920ef11fe879f3d23942ec5ade3a9614.jpg');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (2088, 15, 'Курсы на английском языке по разным тематикам', false, '<p>
















These 3 lectures explain the principles of
integrated and value-based healthcare, the transformation of healthcare systems
and services as well as the role of technology in enabling this process to
reduce improve health outcomes, patient experience and costs. In the first lecture an overview will be provided
of the ‘Principles of integrated and value-based healthcare’. You will learn
why it is important to provide patient centred care and that an integration of
services and collaboration with colleagues from different disciplines is
essential to make this happen. Along with other aspects which are needed to
integrate people and organisations to be focused on patients in a more coherent
way. Also the concept of the patient journey is introduced and related
interaction with the patient as a care team. Lastly, how healthcare services can be optimised. The second lecture elaborates on how do we make the improvements
mentioned in the previous lecture happen. You will learn about how to use
design principles and the patients’ needs to create new care services. You will
discover that process of (re)designing care with all colleagues and other
relevant people involved i.e. co-creation, is the change management itself. The third lecture is about the use of technology, including digital health or
eHealth, to enable care services and realise better health outcomes. Different
examples of how you could use technology in the hospital and in primary care
will be shown. Also, the lecture explains why it is often difficult to make use
of technology successful.



<br></p>', '{28787895}', 'self_paced', 173, 16, 10035, 'en', 'This MOOC is for students and professionals in medicine, healthcare management, finance and economics, ancillary medical services or other students or professionals with an interest in healthcare', false, 4.66666666666667, 'Principles of integrated and value-based healthcare', 'От 4 дней до полутора недель', 'https://stepik.org/media/cache/images/courses/2088/cover/3ff6a13fa90d210e986ad90914a42c2c.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1838, 8, 'Пдд', true, '<p>Этот курс — онлайн-тренажёр, помогающий легко и быстро подготовиться к сдаче теоретического экзамена в ГАИ. 

</p><p>В курсе 

используются официальные экзаменационные билеты ПДД 2017 г. Вы можете решать их бесплатно и неограниченное число раз, а каждый правильный ответ снабжён комментарием, разъясняющим используемое правило.</p><p><span style="background-color: transparent;"><font face="inherit"><span style="font-size: 1em;">Обратите внимание, что система сама будет подбирать вам вопросы, учитывая уровень знаний. Вы можете ей помогать, отмечая, какие </span></font>вопросы<font face="inherit"><span style="font-size: 1em;"> оказались для вас самыми лёгкими, а какие слишком сложными. </span></font></span><br></p><p>Курс можно пройти в специальном мобильном приложении от Stepik: <span style="font-family: inherit; font-size: 1em; background-color: transparent;">для </span><a href="https://itunes.apple.com/ru/app/%D0%BF%D0%B4%D0%B4-2017-%D0%B1%D0%B8%D0%BB%D0%B5%D1%82%D1%8B-%D0%B1%D0%B5%D1%81%D0%BF%D0%BB%D0%B0%D1%82%D0%BD%D1%8B%D0%B9-%D0%B0%D0%B4%D0%B0%D0%BF%D1%82%D0%B8%D0%B2%D0%BD%D1%8B%D0%B9-%D0%BA%D1%83%D1%80%D1%81/id1239082208?l=en&amp;mt=8" rel="nofollow noopener noreferrer" style="font-family: inherit; font-size: 1em;">iOS</a><span style="font-family: inherit; font-size: 1em; background-color: transparent;"> и </span><a href="https://play.google.com/store/apps/details?id=org.stepik.android.adaptive.pdd&amp;hl=ru" rel="nofollow noopener noreferrer" style="font-family: inherit; font-size: 1em;">Android</a><span style="font-family: inherit; font-size: 1em; background-color: transparent;">. </span></p><p><span style="font-family: inherit; font-size: 1em; background-color: transparent;">Удачи!</span></p>', '{45914490}', 'self_paced', 80700, 800, 45970, 'ru', 'NA', false, 4.80952380952381, 'ПДД', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/1838/cover_xP9hIOn/af22a956bf870991635d54a22ee8b53a.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1725, 12, 'Мне повезет', true, '<p>В основе естественных наук лежит эксперимент. И знакомиться с основными законами физики, химии и биологии лучше на практике, а не из учебника. В курсе есть описания и видеозаписи 9 опытов, которые можно сделать дома вместе с детьми. Опыты сопровождаются теорией с анимациями и иллюстрациями. Также есть проверочные и творческие задания.</p>', '{3201}', 'self_paced', 5301, 21, 2683, 'ru', 'Дети 7-14 лет и их родители', false, 4.77272727272727, 'Наука для детей: наглядные опыты дома', 'От 1 до 4 дней', 'https://stepik.org/media/cache/images/courses/1725/cover/e46398ce529bd5e49c35afd69911cc42.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1818, 13, 'Мусор2', true, '<p><b>Основная цель курса</b> — предоставить широкой аудитории информацию об устойчивом развитии.</p><p><b>Устойчивое развитие</b> — это концепция, повсеместная реализация которой поможет человечеству удовлетворять собственные текущие потребности без ущерба для следующих поколений﻿.</p><p>Курс предназначен для всех, кому интересна эта тема, не ограничен ни возрастными, ни образовательными, ни профессиональными рамками. Этот курс:</p><ol><li>Раскроет понятие "устойчивое развитие";<br></li><li>Обозначит основные проблемы, существующие в мире на сегодняшний день;<br></li><li>Продемонстрирует, как концепция уже применяется на практике;<br></li><li>Рассмотрит перспективы устойчивого развития в России﻿.<br></li></ol><p>Участие в курсе <strong>БЕСПЛАТНОЕ</strong>. Дополнительный бонус к курсу: ﻿подготовка к международному тесту грамотности в области устойчивого развития﻿ Sulitest.org.<br>﻿</p><p>Программа курса включает в себя 10 основных блоков, каждый из которых состоит из нескольких лекций продолжительностью 5-15 минут. </p><p>Добро пожаловать! </p><p><span class="image-wrapper"></span></p><p><span class="image-wrapper"></span></p><p style="text-align: center;"><img class="image-fullsize" height="86px" src="https://ucarecdn.com/e1e6d826-4f8e-4399-b586-a9721f2a97f1/" title="Image: https://ucarecdn.com/e1e6d826-4f8e-4399-b586-a9721f2a97f1/" width="256px"></p><br><p><span class="image-wrapper"></span></p><p><a href="http://www.openshkola.org/" rel="nofollow noopener noreferrer" title="Link: http://www.openshkola.org/">www.openshkola.org</a></p><br>', '{25922650}', 'self_paced', 16325, 135, 37678, 'ru', 'Курс предназначен для всех, кому интересна эта тема, не ограничен ни возрастными, ни образовательными, ни профессиональными рамками.', false, 4.78571428571429, 'Устойчивое развитие — стратегия планеты Земля', 'От 3 недель до полутора месяцев', 'https://stepik.org/media/cache/images/courses/1818/cover_m1vbAMy/0d650b307c63402597bf44e17c4d6f85.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1799, 1, 'Мусор1', false, '<p>Курс предназначен для краткого и быстрого введения в курс дела того, кто планирует заниматься моделированием методом молекулярной динамики. Он достаточно универсален, т.к. посвящен не конкретным темам, которые изучаются при помощи данного метода, а ему в целом. Основная его задача - чтобы обучающийся поимел представление о структуре программы, реализующей данный метод, понял основные парадигмы и приемы, использованные при написании данной программы в том числе на примере упрощенной работающей модели. <br></p>', '{23082764}', 'self_paced', 111, 13, 8999, 'ru', 'Студенты - физики, поступающие на кафедру статистической физики или биофизики СПбГУ, а также все желающие', false, 3, 'Введение в метод молекулярной динамики', 'От 4 дней до полутора недель', null);
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1587, 0, 'Языки', false, '<p>Как часто вы писали диктанты в школе? А изучая иностранный язык?<br><br>Если вы любите игры, путешествия или шоппинг и хотите повысить свой уровень английского, а заодно посоревноваться - наш курс специально для вас!</p>', '{135091,1869924}', 'self_paced', 79270, 46, 14958, 'ru', 'NA', false, 3.80952380952381, 'Тренируй английский со Stepik', 'От полутора недель до 3 недель', 'https://stepik.org/media/cache/images/courses/1587/cover/a3674071547e967710aa38ce3c34753d.png');
INSERT INTO public.courses_cluster (id, cluster_x, theme, is_certificate_issued, description, authors, schedule_type, learners_count, quizzes_count, time_to_complete, language, target_audience, is_paid, mean_rating, title, time_completion, cover) VALUES (1571, 17, 'Обучение, управление (кластер не очень)', false, 'Ass', '{16037905}', 'self_paced', 212, 12, 2543, 'ru', 'NA', false, 5, 'Метод Монте-Карло (не закончен)', 'От 1 до 4 дней', null);
